     1→use anyhow::{Context, Result};
     2→use sha2::{Digest, Sha256};
     3→use std::path::{Path, PathBuf};
     4→
     5→use crate::TSPEC_SUFFIX;
     6→use crate::types::Spec;
     7→
     8→/// Load a spec from a TOML file
     9→pub fn load_spec(path: &Path) -> Result<Spec> {
    10→    let content = std::fs::read_to_string(path)
    11→        .with_context(|| format!("failed to read spec file: {}", path.display()))?;
    12→    parse_spec(&content)
    13→}
    14→
    15→/// Parse a spec from TOML string
    16→pub fn parse_spec(toml_str: &str) -> Result<Spec> {
    17→    toml::from_str(toml_str).context("failed to parse spec TOML")
    18→}
    19→
    20→/// Serialize a spec to TOML string (canonical form for hashing)
    21→pub fn serialize_spec(spec: &Spec) -> Result<String> {
    22→    toml::to_string(spec).context("failed to serialize spec")
    23→}
    24→
    25→/// Compute hash of a resolved spec (first 8 hex chars)
    26→pub fn hash_spec(spec: &Spec) -> Result<String> {
    27→    let toml_str = serialize_spec(spec)?;
    28→    let mut hasher = Sha256::new();
    29→    hasher.update(toml_str.as_bytes());
    30→    let result = hasher.finalize();
    31→    Ok(hex::encode(&result[..4]))
    32→}
    33→
    34→/// Save a spec to a TOML file, creating parent directories if needed
    35→pub fn save_spec(spec: &Spec, path: &Path) -> Result<()> {
    36→    if let Some(parent) = path.parent() {
    37→        std::fs::create_dir_all(parent)
    38→            .with_context(|| format!("failed to create directory: {}", parent.display()))?;
    39→    }
    40→    let content = serialize_spec(spec)?;
    41→    std::fs::write(path, &content)
    42→        .with_context(|| format!("failed to write spec file: {}", path.display()))
    43→}
    44→
    45→/// Save a spec snapshot with sequence number and hash: `{name}-{seq:03}-{hash}.toml`
    46→/// Returns the path of the created file.
    47→pub fn save_spec_snapshot(spec: &Spec, name: &str, dir: &Path) -> Result<PathBuf> {
    48→    std::fs::create_dir_all(dir)
    49→        .with_context(|| format!("failed to create directory: {}", dir.display()))?;
    50→
    51→    // Find highest existing sequence number for this name
    52→    let prefix = format!("{}-", name);
    53→    let next_seq = std::fs::read_dir(dir)
    54→        .with_context(|| format!("failed to read directory: {}", dir.display()))?
    55→        .filter_map(|entry| entry.ok())
    56→        .filter_map(|entry| {
    57→            let filename = entry.file_name().to_string_lossy().into_owned();
    58→            if filename.starts_with(&prefix) && filename.ends_with(TSPEC_SUFFIX) {
    59→                // Extract sequence number: {name}-{seq:03}-{hash}{TSPEC_SUFFIX}
    60→                let rest = filename.strip_prefix(&prefix)?;
    61→                let seq_str = rest.split('-').next()?;
    62→                seq_str.parse::<u32>().ok()
    63→            } else {
    64→                None
    65→            }
    66→        })
    67→        .max()
    68→        .map(|n| n + 1)
    69→        .unwrap_or(1);
    70→
    71→    let hash = hash_spec(spec)?;
    72→    let filename = format!("{}-{:03}-{}{}", name, next_seq, hash, TSPEC_SUFFIX);
    73→    let path = dir.join(&filename);
    74→
    75→    let content = serialize_spec(spec)?;
    76→    std::fs::write(&path, &content)
    77→        .with_context(|| format!("failed to write spec file: {}", path.display()))?;
    78→
    79→    Ok(path)
    80→}
