   169→            ..Default::default()
   170→        };
   171→
   172→        let path1 = save_spec_snapshot(&spec1, "test", dir.path()).unwrap();
   173→        let path2 = save_spec_snapshot(&spec2, "test", dir.path()).unwrap();
   174→        let path3 = save_spec_snapshot(&spec1, "test", dir.path()).unwrap();
   175→
   176→        let name1 = path1.file_name().unwrap().to_string_lossy();
   177→        let name2 = path2.file_name().unwrap().to_string_lossy();
   178→        let name3 = path3.file_name().unwrap().to_string_lossy();
   179→
   180→        assert!(name1.starts_with("test-001-"), "got: {}", name1);
   181→        assert!(name2.starts_with("test-002-"), "got: {}", name2);
   182→        assert!(name3.starts_with("test-003-"), "got: {}", name3);
   183→        assert!(name1.ends_with(SUFFIX), "got: {}", name1);
   184→
   185→        // Same content = same hash suffix (strip prefix and SUFFIX)
   186→        let hash1 = name1
   187→            .strip_prefix("test-001-")
   188→            .unwrap()
   189→            .strip_suffix(SUFFIX)
   190→            .unwrap();
   191→        let hash3 = name3
   192→            .strip_prefix("test-003-")
   193→            .unwrap()
   194→            .strip_suffix(SUFFIX)
   195→            .unwrap();
   196→        assert_eq!(hash1, hash3);
   197→    }
   198→}
