   102→/// Check if spec requires nightly toolchain
   103→fn requires_nightly(spec: &Spec) -> bool {
   104→    // High-level panic mode may require nightly
   105→    let panic_needs_nightly = spec.panic.map(|p| p.requires_nightly()).unwrap_or(false);
   106→
   107→    // BuildStd requires nightly
   108→    let has_build_std = !spec.rustc.build_std.is_empty();
   109→
   110→    // Unstable cargo flags require nightly
   111→    let has_unstable = !spec.cargo.unstable.is_empty();
   112→
   113→    panic_needs_nightly || has_build_std || has_unstable
   114→}
   115→
   116→/// Build the base cargo command (with toolchain if needed)
   117→fn build_cargo_command(spec: &Spec) -> Result<Command> {
   118→    let mut cmd = Command::new("cargo");
   119→
   120→    if requires_nightly(spec) {
   121→        cmd.arg("+nightly");
   122→    }
   123→
   124→    Ok(cmd)
   125→}
   126→
   127→/// Apply spec parameters to a cargo command
   128→pub fn apply_spec_to_command(
   129→    cmd: &mut Command,
   130→    spec: &Spec,
   131→    workspace: &Path,
   132→    release: bool,
   133→) -> Result<()> {
   134→    // Handle high-level panic mode (cargo -Z flag)
   135→    if let Some(panic_mode) = spec.panic
   136→        && let Some(z_flag) = panic_mode.cargo_z_flag()
   137→    {
   138→        cmd.arg("-Z").arg(z_flag);
   139→    }
   140→
   141→    // Handle cargo config
   142→    let has_profile = spec.cargo.profile.is_some();
   143→    if let Some(ref profile) = spec.cargo.profile {
   144→        match profile {
   145→            Profile::Release => {
   146→                cmd.arg("--release");
   147→            }
   148→            Profile::Debug => {
   149→                // Debug is default, no flag needed
   150→            }
   151→        }
   152→    }
   153→
   154→    if let Some(ref triple) = spec.cargo.target_triple {
   155→        cmd.arg("--target").arg(triple);
   156→    }
   157→
   158→    if let Some(ref path) = spec.cargo.target_json {
   159→        cmd.arg("--target").arg(path);
   160→    }
   161→
   162→    for flag in &spec.cargo.unstable {
   163→        cmd.arg("-Z").arg(flag);
   164→    }
   165→
   166→    // If no profile in spec but release flag passed, use release
   167→    if !has_profile && release {
   168→        cmd.arg("--release");
   169→    }
   170→
   171→    // Collect rustc flags
   172→    let mut rustc_flags: Vec<String> = Vec::new();
   173→
   174→    // Handle high-level panic mode (rustc -C flag)
   175→    if let Some(panic_mode) = spec.panic
   176→        && let Some(panic_value) = panic_mode.rustc_panic_value()
   177→    {
   178→        rustc_flags.push(format!("-C panic={}", panic_value));
   179→    }
   180→
   181→    // Handle rustc config
   182→    let rustc = &spec.rustc;
   183→
   184→    if let Some(level) = &rustc.opt_level {
   185→        let lvl = match level {
   186→            OptLevel::O0 => "0",
   187→            OptLevel::O1 => "1",
   188→            OptLevel::O2 => "2",
   189→            OptLevel::O3 => "3",
   190→            OptLevel::Os => "s",
   191→            OptLevel::Oz => "z",
   192→        };
   193→        rustc_flags.push(format!("-C opt-level={}", lvl));
   194→    }
   195→
   196→    if let Some(strategy) = &rustc.panic {
   197→        let s = match strategy {
   198→            PanicStrategy::Abort => "abort",
   199→            PanicStrategy::Unwind => "unwind",
   200→            PanicStrategy::ImmediateAbort => "immediate-abort",
   201→        };
   202→        rustc_flags.push(format!("-C panic={}", s));
   203→    }
   204→
   205→    if let Some(true) = rustc.lto {
   206→        rustc_flags.push("-C lto=true".to_string());
   207→    }
   208→
   209→    if let Some(n) = rustc.codegen_units {
   210→        rustc_flags.push(format!("-C codegen-units={}", n));
   211→    }
   212→
   213→    // Handle high-level strip mode
   214→    if let Some(strip_mode) = spec.strip
   215→        && let Some(strip_value) = strip_mode.rustc_strip_value()
   216→    {
   217→        rustc_flags.push(format!("-C strip={}", strip_value));
   218→    }
   219→
   220→    if !rustc.build_std.is_empty() {
   221→        // -Z build-std is a cargo flag, not rustc
   222→        let crates_str = rustc.build_std.join(",");
   223→        cmd.arg("-Z").arg(format!("build-std={}", crates_str));
   224→    }
   225→
   226→    for flag in &rustc.flags {
   227→        rustc_flags.push(flag.clone());
   228→    }
   229→
   230→    // Handle version script (generates file and adds linker arg)
   231→    if let Some(vs) = &spec.linker.version_script {
   232→        let target_dir = workspace.join("target");
   233→        let _ = fs::create_dir_all(&target_dir);
   234→        let version_script_path = target_dir.join("tspec-version.script");
   235→
   236→        // Generate version script: { global: sym1; sym2; local: *; };
   237→        let globals = vs.global.join("; ");
   238→        let content = format!("{{ global: {}; local: {}; }};", globals, vs.local);
   239→
   240→        let mut f =
   241→            fs::File::create(&version_script_path).context("failed to create version script")?;
   242→        writeln!(f, "{}", content)?;
   243→
   244→        rustc_flags.push(format!(
   245→            "-C link-arg=-Wl,--version-script={}",
   246→            version_script_path.display()
   247→        ));
   248→    }
   249→
   250→    // Apply rustc flags (linker args from Args handled by generated build.rs)
   251→    if !rustc_flags.is_empty() {
   252→        cmd.env("RUSTFLAGS", rustc_flags.join(" "));
   253→    }
   254→
   255→    Ok(())
   256→}
