    12→/// Build package(s) with a translation spec
    13→#[derive(Args)]
    14→pub struct BuildCmd {
    15→    /// Package to build (defaults to current directory or all packages)
    16→    #[arg(short = 'p', long = "package")]
    17→    pub package: Option<String>,
    18→    /// Build all packages (even when in a package directory)
    19→    #[arg(short = 'a', long = "all")]
    20→    pub all: bool,
    21→    /// Translation spec to use (defaults to package's tspec file)
    22→    #[arg(short = 't', long = "tspec")]
    23→    pub tspec: Option<String>,
    24→    /// Release build
    25→    #[arg(short, long)]
    26→    pub release: bool,
    27→    /// Strip symbols from binary after build
    28→    #[arg(short, long)]
    29→    pub strip: bool,
    30→    /// Stop on first failure (for all-packages mode)
    31→    #[arg(short, long)]
    32→    pub fail_fast: bool,
    33→}
    34→
    35→impl Execute for BuildCmd {
    36→    fn execute(&self, _project_root: &Path) -> Result<ExitCode> {
    37→        // Resolve package: --all > -p PKG > cwd > all
    38→        let resolved = if self.all {
    39→            None
    40→        } else {
    41→            self.package.clone().or_else(current_package_name)
    42→        };
    43→
    44→        match resolved {
    45→            None => {
    46→                // Build all packages
    47→                let workspace = WorkspaceInfo::discover()?;
    48→                let results = build_all(
    49→                    &workspace,
    50→                    self.tspec.as_deref(),
    51→                    self.release,
    52→                    self.strip,
    53→                    self.fail_fast,
    54→                );
    55→                Ok(print_summary(&results))
    56→            }
    57→            Some(name) => {
    58→                let result = build_crate(&name, self.tspec.as_deref(), self.release)?;
    59→                if self.strip {
    60→                    strip_binary(&result.binary_path)?;
    61→                }
    62→                Ok(ExitCode::SUCCESS)
    63→            }
    64→        }
    65→    }
    66→}
