    10→/// Compare specs for a package (size only)
    11→#[derive(Args)]
    12→pub struct CompareCmd {
    13→    /// Package to compare (required)
    14→    #[arg(short = 'p', long = "package")]
    15→    pub package: String,
    16→    /// Spec file(s) or glob pattern(s) (defaults to tspec* pattern)
    17→    #[arg(short = 't', long = "tspec", action = clap::ArgAction::Append)]
    18→    pub tspec: Vec<String>,
    19→    /// Release build
    20→    #[arg(short, long)]
    21→    pub release: bool,
    22→    /// Strip symbols before comparing sizes
    23→    #[arg(short, long)]
    24→    pub strip: bool,
    25→}
