    27→impl Execute for CompareCmd {
    28→    fn execute(&self, project_root: &Path) -> Result<ExitCode> {
    29→        let package_dir = find_package_dir(project_root, &self.package)?;
    30→        let spec_paths = find_tspecs(&package_dir, &self.tspec)?;
    31→        compare_specs(&self.package, &spec_paths, self.release, self.strip)?;
    32→        Ok(ExitCode::SUCCESS)
    33→    }
    34→}
