     8→
     9→/// Result of building a spec
    10→struct SpecResult {
    11→    name: String,
    12→    size: u64,
    13→}
    14→
    15→/// Compare multiple specs for a crate
    16→pub fn compare_specs(
    17→    crate_name: &str,
    18→    spec_paths: &[impl AsRef<Path> + std::fmt::Debug],
    19→    release: bool,
    20→    strip: bool,
    21→) -> Result<()> {
    22→    println!(
    23→        "Comparing {} builds{}:\n",
    24→        crate_name,
    25→        if strip { " (stripped)" } else { "" }
    26→    );
    27→    //println!("Using specs: {:?}", spec_paths);
    28→
    29→    let mut results = Vec::new();
    30→
    31→    for spec_path in spec_paths {
    32→        let spec_path = spec_path.as_ref();
    33→        let name = spec_path
    34→            .file_name()
    35→            .map(|s| s.to_string_lossy().to_string())
    36→            .unwrap_or_else(|| spec_path.display().to_string());
    37→
    38→        let size = build_spec(crate_name, spec_path, release, strip)?;
    39→        results.push(SpecResult { name, size });
    40→        println!();
    41→    }
    42→
    43→    // Sort by size (smallest first)
    44→    results.sort_by_key(|r| r.size);
    45→
    46→    print_comparison(&results);
    47→
    48→    Ok(())
    49→}
    50→
    51→fn build_spec(crate_name: &str, spec_path: &Path, release: bool, strip: bool) -> Result<u64> {
    52→    let spec_str = spec_path.to_string_lossy();
    53→    println!(
    54→        "  {}:",
    55→        spec_path.file_name().unwrap_or_default().to_string_lossy()
    56→    );
    57→
    58→    // Build
    59→    let build_result = build_crate(crate_name, Some(&spec_str), release)?;
    60→
    61→    // Optionally strip
    62→    if strip {
    63→        strip_binary(&build_result.binary_path)?;
    64→    }
    65→
    66→    // Get size
    67→    let size = binary_size(&build_result.binary_path)?;
    68→    println!("    size: {} bytes", format_size(size));
    69→
    70→    Ok(size)
    71→}
    72→
    73→fn print_comparison(results: &[SpecResult]) {
    74→    let largest_size = results.iter().map(|r| r.size).max().unwrap_or(0);
    75→    let max_name_len = results.iter().map(|r| r.name.len()).max().unwrap_or(4);
    76→
    77→    // Format percent change: show reduction with minus sign, baseline as 0.0%
    78→    let fmt_pct = |size: u64| -> String {
    79→        if largest_size == 0 {
    80→            return "   0.0%".to_string();
    81→        }
    82→        let pct = ((largest_size as f64 - size as f64) / largest_size as f64) * 100.0;
    83→        if pct > 0.0 {
    84→            format!("{:>7.1}%", -pct)
    85→        } else {
    86→            "   0.0%".to_string()
    87→        }
    88→    };
    89→
    90→    println!();
    91→    print_header!("COMPARE SUMMARY");
    92→    println!(
    93→        "  {:width$}  {:>10}  {:>8}",
    94→        "Spec",
    95→        "Size",
    96→        "Change",
    97→        width = max_name_len
    98→    );
    99→    for result in results {
   100→        println!(
   101→            "  {:width$}  {:>10}  {}",
   102→            result.name,
   103→            format_size(result.size),
   104→            fmt_pct(result.size),
   105→            width = max_name_len
   106→        );
   107→    }
   108→    print_hline!();
   109→    println!();
   110→}
   111→
   112→fn format_size(bytes: u64) -> String {
   113→    if bytes >= 1_000_000 {
   114→        format!("{:.3}M", bytes as f64 / 1_000_000.0)
   115→    } else if bytes >= 1_000 {
   116→        format!("{:.3}K", bytes as f64 / 1_000.0)
   117→    } else {
   118→        format!("{}", bytes)
   119→    }
   120→}
