    15→/// Result of a batch operation on a single crate
    16→pub struct OpResult {
    17→    pub name: String,
    18→    pub success: bool,
    19→    pub message: String,
    20→    pub size: Option<u64>,
    21→}
    22→
    23→/// Build all workspace crates (excluding build tools)
    24→pub fn build_all(
    25→    workspace: &WorkspaceInfo,
    26→    tspec: Option<&str>,
    27→    release: bool,
    28→    strip: bool,
    29→    fail_fast: bool,
    30→) -> Vec<OpResult> {
    31→    let mut results = Vec::new();
    32→
    33→    for member in workspace.buildable_members() {
    34→        println!("=== {} ===", member.name);
    35→
    36→        let result = match build_crate(&member.name, tspec, release) {
    37→            Ok(build_result) => {
    38→                if strip
    39→                    && member.has_binary
    40→                    && let Err(e) = strip_binary(&build_result.binary_path)
    41→                {
    42→                    eprintln!("  warning: strip failed: {}", e);
    43→                }
    44→                let size = binary_size(&build_result.binary_path).ok();
    45→                OpResult {
    46→                    name: member.name.clone(),
    47→                    success: true,
    48→                    message: format!("{}", build_result.binary_path.display()),
    49→                    size,
    50→                }
    51→            }
    52→            Err(e) => OpResult {
    53→                name: member.name.clone(),
    54→                success: false,
    55→                message: e.to_string(),
    56→                size: None,
    57→            },
    58→        };
    59→
    60→        let failed = !result.success;
    61→        results.push(result);
    62→
    63→        if failed && fail_fast {
    64→            break;
    65→        }
    66→    }
    67→
    68→    results
    69→}
    70→
    71→/// Run all app crates sequentially
    72→pub fn run_all(
    73→    workspace: &WorkspaceInfo,
    74→    tspec: Option<&str>,
    75→    release: bool,
    76→    strip: bool,
    77→) -> Vec<OpResult> {
    78→    let mut results = Vec::new();
    79→
    80→    for member in workspace.runnable_members() {
    81→        println!("=== {} ===", member.name);
    82→
    83→        let result = match build_crate(&member.name, tspec, release) {
    84→            Ok(build_result) => {
    85→                if strip && let Err(e) = strip_binary(&build_result.binary_path) {
    86→                    eprintln!("  warning: strip failed: {}", e);
    87→                }
    88→                match run_binary(&build_result.binary_path, &[]) {
    89→                    Ok(exit_code) => OpResult {
    90→                        name: member.name.clone(),
    91→                        success: true, // We don't treat non-zero exit as failure
    92→                        message: format!("exit code: {}", exit_code),
    93→                        size: None,
    94→                    },
    95→                    Err(e) => OpResult {
    96→                        name: member.name.clone(),
    97→                        success: false,
    98→                        message: format!("run failed: {}", e),
    99→                        size: None,
   100→                    },
   101→                }
   102→            }
   103→            Err(e) => OpResult {
   104→                name: member.name.clone(),
   105→                success: false,
   106→                message: format!("build failed: {}", e),
   107→                size: None,
   108→            },
   109→        };
   110→
   111→        results.push(result);
   112→    }
   113→
   114→    results
   115→}
   116→
   117→/// Test all workspace crates
   118→pub fn test_all(
   119→    workspace: &WorkspaceInfo,
   120→    tspec: Option<&str>,
   121→    release: bool,
   122→    fail_fast: bool,
   123→) -> Vec<OpResult> {
   124→    let mut results = Vec::new();
   125→
   126→    // Test regular crates (excluding Test kind which needs special handling)
   127→    for member in workspace.buildable_members() {
   128→        if member.kind == CrateKind::Test {
   129→            continue; // Handle test crates separately
   130→        }
   131→
   132→        println!("=== {} ===", member.name);
   133→
   134→        let result = match test_crate(&member.name, tspec, release) {
   135→            Ok(()) => OpResult {
   136→                name: member.name.clone(),
   137→                success: true,
   138→                message: "ok".to_string(),
   139→                size: None,
   140→            },
   141→            Err(e) => OpResult {
   142→                name: member.name.clone(),
   143→                success: false,
   144→                message: e.to_string(),
   145→                size: None,
   146→            },
   147→        };
   148→
   149→        let failed = !result.success;
   150→        results.push(result);
   151→
   152→        if failed && fail_fast {
   153→            return results;
   154→        }
   155→    }
   156→
   157→    // Handle test crates (like rlibc-x2-tests) - build and run all test binaries
   158→    for member in workspace.test_members() {
   159→        println!("=== {} ===", member.name);
   160→
   161→        // Build the test crate (builds all binaries)
   162→        if let Err(e) = build_crate(&member.name, tspec, release) {
   163→            results.push(OpResult {
   164→                name: member.name.clone(),
   165→                success: false,
   166→                message: format!("build failed: {}", e),
   167→                size: None,
   168→            });
   169→            if fail_fast {
   170→                return results;
   171→            }
   172→            continue;
   173→        }
   174→
   175→        // Find and run all test binaries in the target directory
   176→        let profile = if release { "release" } else { "debug" };
   177→        let target_dir = workspace.root.join("target").join(profile);
   178→
   179→        // Look for binaries that end with "-tests" in the target directory
   180→        let test_binaries: Vec<_> = std::fs::read_dir(&target_dir)
   181→            .into_iter()
   182→            .flatten()
   183→            .filter_map(|e| e.ok())
   184→            .filter(|e| {
   185→                let name = e.file_name().to_string_lossy().to_string();
   186→                let is_file = e.path().is_file();
   187→                let is_test = name.ends_with("-tests");
   188→                let is_executable = e
   189→                    .metadata()
   190→                    .map(|m| m.permissions().mode() & 0o111 != 0)
   191→                    .unwrap_or(false);
   192→                is_file && is_test && is_executable
   193→            })
   194→            .collect();
   195→
   196→        if test_binaries.is_empty() {
   197→            results.push(OpResult {
   198→                name: member.name.clone(),
   199→                success: false,
   200→                message: "no test binaries found".to_string(),
   201→                size: None,
   202→            });
   203→            continue;
   204→        }
   205→
   206→        for entry in test_binaries {
   207→            let path = entry.path();
