    35→fn run() -> Result<ExitCode> {
    36→    let cli = Cli::parse();
    37→
    38→    match cli.command {
    39→        Commands::Build {
    40→            package,
    41→            all,
    42→            tspec,
    43→            release,
    44→            strip,
    45→            fail_fast,
    46→        } => {
    47→            // Resolve package: --all > -p PKG > cwd > all
    48→            let resolved = if all {
    49→                None
    50→            } else {
    51→                package.or_else(current_package_name)
    52→            };
    53→            match resolved {
    54→                None => {
    55→                    // Build all packages
    56→                    let workspace = WorkspaceInfo::discover()?;
    57→                    let results =
    58→                        build_all(&workspace, tspec.as_deref(), release, strip, fail_fast);
    59→                    return Ok(print_summary(&results));
    60→                }
    61→                Some(name) => {
    62→                    let result = build_crate(&name, tspec.as_deref(), release)?;
    63→                    if strip {
    64→                        strip_binary(&result.binary_path)?;
    65→                    }
    66→                }
    67→            }
    68→        }
    69→        Commands::Run {
    70→            package,
    71→            all,
    72→            tspec,
    73→            release,
    74→            strip,
    75→            args,
    76→        } => {
    77→            // Resolve package: --all > -p PKG > cwd > all
    78→            let resolved = if all {
    79→                None
    80→            } else {
    81→                package.or_else(current_package_name)
    82→            };
    83→            match resolved {
    84→                None => {
    85→                    // Run all apps (args not supported for --all)
    86→                    let workspace = WorkspaceInfo::discover()?;
    87→                    let results = run_all(&workspace, tspec.as_deref(), release, strip);
    88→                    return Ok(print_run_summary(&results));
    89→                }
    90→                Some(name) => {
    91→                    // Build, optionally strip, then run
    92→                    let result = build_crate(&name, tspec.as_deref(), release)?;
    93→                    if strip {
    94→                        strip_binary(&result.binary_path)?;
    95→                    }
    96→                    let exit_code = run_binary(&result.binary_path, &args)?;
    97→                    std::process::exit(exit_code);
    98→                }
    99→            }
   100→        }
   101→        Commands::Test {
   102→            package,
   103→            all,
   104→            tspec,
   105→            release,
   106→            fail_fast,
   107→        } => {
   108→            // Resolve package: --all > -p PKG > cwd > all
   109→            let resolved = if all {
   110→                None
   111→            } else {
   112→                package.or_else(current_package_name)
   113→            };
   114→            match resolved {
   115→                None => {
   116→                    // Test all packages
   117→                    let workspace = WorkspaceInfo::discover()?;
   118→                    let results = test_all(&workspace, tspec.as_deref(), release, fail_fast);
   119→                    return Ok(print_test_summary(&results));
   120→                }
   121→                Some(name) => {
   122→                    test_crate(&name, tspec.as_deref(), release)?;
   123→                }
   124→            }
   125→        }
   126→        Commands::Clean { package, release } => {
   127→            CleanCmd {
   128→                workspace: find_project_root()?,
   129→                package,
   130→                release,
   131→            }
   132→            .execute()?;
   133→        }
   134→        Commands::Compare {
   135→            package,
   136→            tspec,
   137→            release,
   138→            strip,
   139→        } => {
   140→            let workspace = find_project_root()?;
   141→            let package_dir = find_package_dir(&workspace, &package)?;
   142→            let spec_paths = find_tspecs(&package_dir, &tspec)?;
   143→            compare_specs(&package, &spec_paths, release, strip)?;
   144→        }
   145→        Commands::Compat { package, spec } => {
   146→            match spec {
   147→                Some(s) => println!("compat add: package={package} spec={s}"),
   148→                None => println!("compat show: package={package}"),
   149→            }
   150→            // TODO: implement
   151→        }
   152→        Commands::Incompat { package, spec } => {
   153→            println!("incompat add: package={package} spec={spec}");
   154→            // TODO: implement
   155→        }
   156→        Commands::Ts { command } => match command {
   157→            TsCommands::List { package, all } => {
   158→                ts_cmd::list_tspecs(package.as_deref(), all)?;
   159→            }
   160→            TsCommands::Show {
   161→                package,
   162→                all,
   163→                tspec,
   164→            } => {
   165→                ts_cmd::show_tspec(package.as_deref(), all, tspec.as_deref())?;
   166→            }
   167→            TsCommands::Hash {
   168→                package,
   169→                all,
   170→                tspec,
   171→            } => {
   172→                ts_cmd::hash_tspec(package.as_deref(), all, tspec.as_deref())?;
   173→            }
   174→            TsCommands::New {
   175→                name,
   176→                package,
   177→                from,
   178→            } => {
   179→                ts_cmd::new_tspec(package.as_deref(), &name, from.as_deref())?;
   180→            }
   181→            TsCommands::Set {
   182→                assignment,
   183→                package,
   184→                tspec,
   185→            } => {
   186→                let (key, value) = assignment.split_once('=').ok_or_else(|| {
   187→                    anyhow::anyhow!("invalid assignment '{}': expected key=value", assignment)
   188→                })?;
   189→                ts_cmd::set_value(package.as_deref(), key, value, tspec.as_deref())?;
   190→            }
   191→        },
   192→        Commands::Version => {
   193→            println!("tspec {}", env!("CARGO_PKG_VERSION"));
   194→        }
   195→        Commands::Install { path, force } => {
   196→            let resolved = path
   197→                .canonicalize()
   198→                .with_context(|| format!("path not found: {}", path.display()))?;
   199→            let mut cmd = std::process::Command::new("cargo");
   200→            cmd.arg("install").arg("--path").arg(&resolved);
   201→            if force {
   202→                cmd.arg("--force");
   203→            }
   204→            let status = cmd.status().context("failed to run cargo install")?;
   205→            if !status.success() {
   206→                anyhow::bail!("cargo install failed");
   207→            }
   208→        }
   209→    }
   210→
   211→    Ok(ExitCode::SUCCESS)
   212→}
