     5→
     6→/// Trait for commands that wrap cargo subcommands with minimal logic.
     7→pub trait CargoPassthrough {
     8→    /// The cargo subcommand to run (e.g., "clean", "clippy", "fmt")
     9→    fn subcommand(&self) -> &str;
    10→
    11→    /// Arguments to pass to the cargo subcommand
    12→    fn args(&self) -> Vec<OsString>;
    13→
    14→    /// Working directory for the command (defaults to current directory)
    15→    fn workdir(&self) -> Option<&Path> {
    16→        None
    17→    }
    18→
    19→    /// Execute the cargo command with default implementation
    20→    fn execute(&self) -> Result<ExitCode> {
    21→        let mut cmd = std::process::Command::new("cargo");
    22→        cmd.arg(self.subcommand());
    23→        cmd.args(self.args());
    24→        if let Some(dir) = self.workdir() {
    25→            cmd.current_dir(dir);
    26→        }
    27→        let status = cmd
    28→            .status()
    29→            .with_context(|| format!("failed to run cargo {}", self.subcommand()))?;
    30→        if status.success() {
    31→            Ok(ExitCode::SUCCESS)
    32→        } else {
    33→            bail!("cargo {} failed", self.subcommand());
    34→        }
    35→    }
    36→}
