    38→/// Clean build artifacts
    39→pub struct CleanCmd {
    40→    pub workspace: PathBuf,
    41→    pub package: Option<String>,
    42→    pub release: bool,
    43→}
    44→
    45→impl CargoPassthrough for CleanCmd {
    46→    fn subcommand(&self) -> &str {
    47→        "clean"
    48→    }
    49→
    50→    fn args(&self) -> Vec<OsString> {
    51→        let mut args = Vec::new();
    52→        if let Some(pkg) = &self.package {
    53→            args.push("-p".into());
    54→            args.push(pkg.into());
    55→        }
    56→        if self.release {
    57→            args.push("--release".into());
    58→        }
    59→        args
    60→    }
    61→
    62→    fn workdir(&self) -> Option<&Path> {
    63→        Some(&self.workspace)
    64→    }
    65→}
