     1→//! Workspace discovery and crate classification
     2→//!
     3→//! Uses `cargo metadata` to discover workspace members and classify them
     4→//! by type (app, lib, tool, test, build tool).
     5→
     6→use anyhow::{Context, Result};
     7→use cargo_metadata::MetadataCommand;
     8→use std::path::{Path, PathBuf};
     9→
    10→/// Crate classification for behavior differences
    11→#[derive(Debug, Clone, PartialEq, Eq)]
    12→pub enum CrateKind {
    13→    /// apps/* - application binaries, runnable
    14→    App,
    15→    /// libs/* - library crates
    16→    Lib,
    17→    /// tools/* - utility binaries
    18→    Tool,
    19→    /// */tests - test binary crates (special handling)
    20→    Test,
    21→    /// Build tools such as xtask or tspec, excluded by default
    22→    BuildTool,
    23→}
    24→
    25→/// Information about a workspace member
    26→#[derive(Debug, Clone)]
    27→pub struct CrateMember {
    28→    pub name: String,
    29→    pub path: PathBuf,
    30→    pub has_binary: bool,
    31→    pub kind: CrateKind,
    32→}
    33→
    34→/// Workspace information from cargo metadata
    35→pub struct WorkspaceInfo {
    36→    pub root: PathBuf,
    37→    pub members: Vec<CrateMember>,
    38→}
    39→
    40→impl WorkspaceInfo {
    41→    /// Discover workspace using cargo metadata
    42→    pub fn discover() -> Result<Self> {
    43→        let metadata = MetadataCommand::new()
    44→            .no_deps()
    45→            .exec()
    46→            .context("failed to run cargo metadata")?;
    47→
    48→        let root = metadata.workspace_root.as_std_path().to_path_buf();
    49→
    50→        let members: Vec<CrateMember> = metadata
    51→            .workspace_packages()
    52→            .iter()
    53→            .map(|pkg| {
    54→                let path = pkg
    55→                    .manifest_path
    56→                    .parent()
    57→                    .unwrap()
    58→                    .as_std_path()
    59→                    .to_path_buf();
    60→                let has_binary = pkg.targets.iter().any(|t| t.is_bin());
    61→                let kind = classify_crate(&path, &pkg.name);
    62→
    63→                CrateMember {
    64→                    name: pkg.name.clone(),
    65→                    path,
    66→                    has_binary,
    67→                    kind,
    68→                }
    69→            })
    70→            .collect();
    71→
    72→        Ok(WorkspaceInfo { root, members })
    73→    }
    74→
    75→    /// Get members excluding build tools such as xtask or tspec
    76→    pub fn buildable_members(&self) -> Vec<&CrateMember> {
    77→        self.members
    78→            .iter()
    79→            .filter(|m| m.kind != CrateKind::BuildTool)
    80→            .collect()
    81→    }
    82→
    83→    /// Get members that can be run (apps only - have binaries and are runnable)
    84→    pub fn runnable_members(&self) -> Vec<&CrateMember> {
    85→        self.members
    86→            .iter()
    87→            .filter(|m| m.has_binary && m.kind == CrateKind::App)
    88→            .collect()
    89→    }
    90→
    91→    /// Get test crates (special handling for rlibc-x2-tests etc.)
    92→    pub fn test_members(&self) -> Vec<&CrateMember> {
    93→        self.members
    94→            .iter()
    95→            .filter(|m| m.kind == CrateKind::Test)
    96→            .collect()
    97→    }
    98→}
    99→
   100→/// Classify a crate based on its path and name
   101→fn classify_crate(path: &Path, name: &str) -> CrateKind {
   102→    let path_str = path.to_string_lossy();
   103→
   104→    // Build tools at workspace root level
   105→    if name == "tspec" || name == "xt" || name == "xtask" {
   106→        return CrateKind::BuildTool;
   107→    }
   108→
   109→    // Test crates
   110→    if path_str.contains("/tests") || name.ends_with("-tests") {
   111→        return CrateKind::Test;
   112→    }
   113→
   114→    // Categorize by directory
   115→    if path_str.contains("apps/") {
   116→        CrateKind::App
   117→    } else if path_str.contains("libs/") {
   118→        CrateKind::Lib
   119→    } else if path_str.contains("tools/") {
   120→        CrateKind::Tool
   121→    } else {
   122→        // Unknown location, treat as build tool (excluded)
   123→        CrateKind::BuildTool
   124→    }
   125→}
   126→
   127→#[cfg(test)]
   128→mod tests {
   129→    use super::*;
   130→
   131→    #[test]
   132→    fn classify_app() {
   133→        let path = PathBuf::from("/workspace/apps/ex-x1");
   134→        assert_eq!(classify_crate(&path, "ex-x1"), CrateKind::App);
   135→    }
   136→
   137→    #[test]
   138→    fn classify_lib() {
   139→        let path = PathBuf::from("/workspace/libs/rlibc-x1");
   140→        assert_eq!(classify_crate(&path, "rlibc-x1"), CrateKind::Lib);
   141→    }
   142→
   143→    #[test]
   144→    fn classify_tool() {
   145→        let path = PathBuf::from("/workspace/tools/is-libc-used");
   146→        assert_eq!(classify_crate(&path, "is-libc-used"), CrateKind::Tool);
   147→    }
   148→
   149→    #[test]
   150→    fn classify_test_by_path() {
   151→        let path = PathBuf::from("/workspace/libs/rlibc-x2/tests");
   152→        assert_eq!(classify_crate(&path, "rlibc-x2-tests"), CrateKind::Test);
   153→    }
   154→
   155→    #[test]
   156→    fn classify_test_by_name() {
   157→        let path = PathBuf::from("/workspace/somewhere");
   158→        assert_eq!(classify_crate(&path, "foo-tests"), CrateKind::Test);
   159→    }
   160→
   161→    #[test]
   162→    fn classify_build_tool_xt() {
   163→        let path = PathBuf::from("/workspace/xt");
   164→        assert_eq!(classify_crate(&path, "xt"), CrateKind::BuildTool);
   165→    }
   166→
   167→    #[test]
   168→    fn classify_build_tool_xtask() {
   169→        let path = PathBuf::from("/workspace/xtask");
   170→        assert_eq!(classify_crate(&path, "xtask"), CrateKind::BuildTool);
   171→    }
   172→
   173→    #[test]
   174→    fn discover_works() {
   175→        // This test works for both workspaces and POPs
   176→        let info = WorkspaceInfo::discover();
   177→        if let Ok(info) = info {
   178→            // Should have some members (at least 1 for POP, more for workspace)
   179→            assert!(!info.members.is_empty());
   180→
   181→            // xt and xtask should be excluded from buildable
   182→            let buildable = info.buildable_members();
   183→            assert!(
   184→                buildable
   185→                    .iter()
   186→                    .all(|m| m.name != "xt" && m.name != "xtask")
   187→            );
   188→
   189→            // For workspaces with apps/, should have runnable members
   190→            // For POPs or workspaces without apps/, runnable may be empty
   191→            let _runnable = info.runnable_members();
   192→            // Just verify it doesn't panic - count depends on project structure
   193→        }
   194→        // If cargo metadata fails (e.g., not in a Rust project), that's OK for CI
   195→    }
   196→}
