     1→//! High-level build options that expand to lower-level flags.
     2→//!
     3→//! These provide "easy generality" - user picks intent, implementation
     4→//! details (which cargo/rustc flags to set) are handled automatically.
     5→
     6→use serde::{Deserialize, Serialize};
     7→
     8→/// Panic handling strategy.
     9→///
    10→/// This is a high-level option that automatically sets the appropriate
    11→/// cargo and rustc flags.
    12→#[derive(Debug, Clone, Copy, Default, PartialEq, Eq, Serialize, Deserialize)]
    13→#[serde(rename_all = "kebab-case")]
    14→pub enum PanicMode {
    15→    /// Default Rust behavior - panic unwinds the stack.
    16→    #[default]
    17→    Unwind,
    18→
    19→    /// Panic aborts immediately (no unwinding).
    20→    /// Sets: rustc -C panic=abort
    21→    Abort,
    22→
    23→    /// Panic aborts with no formatting overhead (nightly only).
    24→    /// Sets: cargo -Z panic-immediate-abort AND rustc -C panic=immediate-abort
    25→    /// This eliminates all panic formatting machinery for smallest binaries.
    26→    ImmediateAbort,
    27→}
    28→
    29→impl PanicMode {
    30→    /// Returns true if this mode requires nightly toolchain.
    31→    pub fn requires_nightly(&self) -> bool {
    32→        matches!(self, PanicMode::ImmediateAbort)
    33→    }
    34→
    35→    /// Returns the cargo -Z flag if needed.
    36→    pub fn cargo_z_flag(&self) -> Option<&'static str> {
    37→        match self {
    38→            PanicMode::ImmediateAbort => Some("panic-immediate-abort"),
    39→            _ => None,
    40→        }
    41→    }
    42→
    43→    /// Returns the rustc -C panic= value.
    44→    pub fn rustc_panic_value(&self) -> Option<&'static str> {
    45→        match self {
    46→            PanicMode::Unwind => None, // default, no flag needed
    47→            PanicMode::Abort => Some("abort"),
    48→            PanicMode::ImmediateAbort => Some("immediate-abort"),
    49→        }
    50→    }
    51→}
    52→
    53→/// Symbol stripping mode.
    54→///
    55→/// This is a high-level option that sets the rustc strip flag.
    56→#[derive(Debug, Clone, Copy, Default, PartialEq, Eq, Serialize, Deserialize)]
    57→#[serde(rename_all = "kebab-case")]
    58→pub enum StripMode {
    59→    /// No stripping (default).
    60→    #[default]
    61→    None,
    62→
    63→    /// Strip debug info only.
    64→    /// Sets: rustc -C strip=debuginfo
    65→    Debuginfo,
    66→
    67→    /// Strip all symbols.
    68→    /// Sets: rustc -C strip=symbols
    69→    Symbols,
    70→}
    71→
    72→impl StripMode {
    73→    /// Returns the rustc -C strip= value, if any.
    74→    pub fn rustc_strip_value(&self) -> Option<&'static str> {
    75→        match self {
    76→            StripMode::None => None,
    77→            StripMode::Debuginfo => Some("debuginfo"),
    78→            StripMode::Symbols => Some("symbols"),
    79→        }
    80→    }
    81→}
    82→
    83→#[cfg(test)]
    84→mod tests {
    85→    use super::*;
    86→
    87→    #[test]
    88→    fn unwind_is_default() {
    89→        assert_eq!(PanicMode::default(), PanicMode::Unwind);
    90→    }
    91→
    92→    #[test]
    93→    fn only_immediate_abort_requires_nightly() {
    94→        assert!(!PanicMode::Unwind.requires_nightly());
    95→        assert!(!PanicMode::Abort.requires_nightly());
    96→        assert!(PanicMode::ImmediateAbort.requires_nightly());
    97→    }
    98→
    99→    #[test]
   100→    fn cargo_z_flag_only_for_immediate_abort() {
   101→        assert_eq!(PanicMode::Unwind.cargo_z_flag(), None);
   102→        assert_eq!(PanicMode::Abort.cargo_z_flag(), None);
   103→        assert_eq!(
   104→            PanicMode::ImmediateAbort.cargo_z_flag(),
   105→            Some("panic-immediate-abort")
   106→        );
   107→    }
   108→
   109→    #[test]
   110→    fn rustc_panic_values() {
   111→        assert_eq!(PanicMode::Unwind.rustc_panic_value(), None);
   112→        assert_eq!(PanicMode::Abort.rustc_panic_value(), Some("abort"));
   113→        assert_eq!(
   114→            PanicMode::ImmediateAbort.rustc_panic_value(),
   115→            Some("immediate-abort")
   116→        );
   117→    }
   118→
   119→    #[test]
   120→    fn strip_none_is_default() {
   121→        assert_eq!(StripMode::default(), StripMode::None);
   122→    }
   123→
   124→    #[test]
