        }
        Commands::Compat { package, spec } => {
            match spec {
                Some(s) => println!("compat add: package={package} spec={s}"),
                None => println!("compat show: package={package}"),
            }
            // TODO: implement
        }
        Commands::Incompat { package, spec } => {
