                    let results =
                        build_all(&workspace, tspec.as_deref(), release, strip, fail_fast);
                    return Ok(print_summary(&results));
                }
                Some(name) => {
                    let result = build_crate(&name, tspec.as_deref(), release)?;
                    if strip {
                        strip_binary(&result.binary_path)?;
                    }
                }
            }
        }
