     1→use anyhow::{Context, Result};
     2→use clap::Parser;
     3→use std::process::ExitCode;
     4→
     5→use tspec::all::{build_all, print_run_summary, print_summary, run_all};
     6→use tspec::binary::strip_binary;
     7→use tspec::cargo_build::build_crate;
     8→use tspec::cargo_cmd::CargoPassthrough;
     9→use tspec::cli::{Cli, Commands, TsCommands};
    10→use tspec::compare::compare_specs;
    11→use tspec::find_paths::{find_package_dir, find_project_root, find_tspecs, get_crate_name};
    12→use tspec::run::run_binary;
    13→use tspec::ts_cmd;
    14→use tspec::workspace::WorkspaceInfo;
    15→
    16→fn main() -> ExitCode {
    17→    match run() {
    18→        Ok(code) => code,
    19→        Err(e) => {
    20→            eprintln!("error: {e}");
    21→            ExitCode::from(1)
    22→        }
    23→    }
    24→}
    25→
    26→/// Check if current directory is a package (has Cargo.toml with [package])
    27→fn current_package_name() -> Option<String> {
    28→    let cwd = std::env::current_dir().ok()?;
    29→    get_crate_name(&cwd).ok()
    30→}
    31→
    32→fn run() -> Result<ExitCode> {
    33→    let cli = Cli::parse();
    34→
    35→    match cli.command {
    36→        Commands::Build {
    37→            package,
    38→            all,
    39→            tspec,
    40→            release,
    41→            strip,
    42→            fail_fast,
    43→        } => {
    44→            // Resolve package: --all > -p PKG > cwd > all
    45→            let resolved = if all {
    46→                None
    47→            } else {
    48→                package.or_else(current_package_name)
    49→            };
    50→            match resolved {
    51→                None => {
    52→                    // Build all packages
    53→                    let workspace = WorkspaceInfo::discover()?;
    54→                    let results =
    55→                        build_all(&workspace, tspec.as_deref(), release, strip, fail_fast);
    56→                    return Ok(print_summary(&results));
    57→                }
    58→                Some(name) => {
    59→                    let result = build_crate(&name, tspec.as_deref(), release)?;
    60→                    if strip {
    61→                        strip_binary(&result.binary_path)?;
    62→                    }
    63→                }
    64→            }
    65→        }
    66→        Commands::Run {
    67→            package,
    68→            all,
    69→            tspec,
    70→            release,
    71→            strip,
    72→            args,
    73→        } => {
    74→            // Resolve package: --all > -p PKG > cwd > all
    75→            let resolved = if all {
    76→                None
    77→            } else {
    78→                package.or_else(current_package_name)
    79→            };
    80→            match resolved {
    81→                None => {
    82→                    // Run all apps (args not supported for --all)
    83→                    let workspace = WorkspaceInfo::discover()?;
    84→                    let results = run_all(&workspace, tspec.as_deref(), release, strip);
    85→                    return Ok(print_run_summary(&results));
    86→                }
    87→                Some(name) => {
    88→                    // Build, optionally strip, then run
    89→                    let result = build_crate(&name, tspec.as_deref(), release)?;
    90→                    if strip {
    91→                        strip_binary(&result.binary_path)?;
    92→                    }
    93→                    let exit_code = run_binary(&result.binary_path, &args)?;
    94→                    std::process::exit(exit_code);
    95→                }
    96→            }
    97→        }
    98→        Commands::Test(cmd) => {
    99→            return cmd.execute(&find_project_root()?);
   100→        }
   101→        Commands::Clean(cmd) => {
   102→            cmd.execute(&find_project_root()?)?;
   103→        }
   104→        Commands::Compare {
   105→            package,
   106→            tspec,
   107→            release,
   108→            strip,
   109→        } => {
   110→            let workspace = find_project_root()?;
   111→            let package_dir = find_package_dir(&workspace, &package)?;
   112→            let spec_paths = find_tspecs(&package_dir, &tspec)?;
   113→            compare_specs(&package, &spec_paths, release, strip)?;
   114→        }
   115→        Commands::Compat { package, spec } => {
   116→            match spec {
   117→                Some(s) => println!("compat add: package={package} spec={s}"),
   118→                None => println!("compat show: package={package}"),
   119→            }
   120→            // TODO: implement
   121→        }
   122→        Commands::Incompat { package, spec } => {
   123→            println!("incompat add: package={package} spec={spec}");
   124→            // TODO: implement
   125→        }
   126→        Commands::Ts { command } => match command {
   127→            TsCommands::List { package, all } => {
   128→                ts_cmd::list_tspecs(package.as_deref(), all)?;
   129→            }
   130→            TsCommands::Show {
   131→                package,
   132→                all,
   133→                tspec,
   134→            } => {
   135→                ts_cmd::show_tspec(package.as_deref(), all, tspec.as_deref())?;
   136→            }
