234→#[unsafe(no_mangle)]
235→pub extern "C" fn malloc(size: usize) -> *mut u8 {
236→    unsafe {
237→        let aligned_size = (size + 15) & !15;
238→        let new_current = HEAP_CURRENT.add(aligned_size);
239→
240→        if new_current > HEAP_END {
241→            let grow_size = (aligned_size + 4095) & !4095;
242→            let new_end = brk(HEAP_END.add(grow_size));
243→            if new_end <= HEAP_END {
244→                return core::ptr::null_mut();
245→            }
246→            HEAP_END = new_end;
247→        }
248→
249→        let ptr = HEAP_CURRENT;
250→        HEAP_CURRENT = new_current;
251→        ptr
252→    }
253→}
