255→/// Reallocate memory.
256→///
257→/// # Safety
258→/// If `ptr` is non-null, it must have been returned by a previous call to
259→/// `malloc`, `realloc`, or `calloc`, and `size` must not exceed the original
260→/// allocation size (this simple allocator doesn't track sizes).
261→#[unsafe(no_mangle)]
262→pub unsafe extern "C" fn realloc(ptr: *mut u8, size: usize) -> *mut u8 {
263→    if ptr.is_null() {
264→        return malloc(size);
265→    }
266→    let new_ptr = malloc(size);
267→    if !new_ptr.is_null() {
268→        unsafe {
269→            core::ptr::copy_nonoverlapping(ptr, new_ptr, size);
270→        }
271→    }
272→    new_ptr
273→}
