276→pub extern "C" fn calloc(nmemb: usize, size: usize) -> *mut u8 {
277→    let total = nmemb.saturating_mul(size);
278→    let ptr = malloc(total);
279→    if !ptr.is_null() {
280→        unsafe {
281→            core::ptr::write_bytes(ptr, 0, total);
282→        }
283→    }
284→    ptr
