117→/// Test all workspace packages
118→pub fn test_all(
119→    workspace: &WorkspaceInfo,
120→    tspec: Option<&str>,
121→    release: bool,
122→    fail_fast: bool,
123→) -> Vec<OpResult> {
124→    let mut results = Vec::new();
125→
126→    // Test regular packages (excluding Test kind which needs special handling)
127→    for member in workspace.buildable_members() {
128→        if member.kind == PackageKind::Test {
129→            continue; // Handle test packages separately
130→        }
131→
132→        println!("=== {} ===", member.name);
133→
134→        let result = match test_package(&member.name, tspec, release) {
135→            Ok(()) => OpResult {
136→                name: member.name.clone(),
137→                success: true,
138→                message: "ok".to_string(),
139→                size: None,
140→            },
141→            Err(e) => OpResult {
142→                name: member.name.clone(),
143→                success: false,
144→                message: e.to_string(),
145→                size: None,
146→            },
147→        };
148→
149→        let failed = !result.success;
150→        results.push(result);
151→
152→        if failed && fail_fast {
153→            return results;
154→        }
155→    }
156→
157→    // Handle test packages (like rlibc-x2-tests) - build and run all test binaries
158→    for member in workspace.test_members() {
159→        println!("=== {} ===", member.name);
160→
161→        // Build the test package (builds all binaries)
162→        let build_result = match build_package(&member.name, tspec, release) {
163→            Ok(r) => r,
164→            Err(e) => {
165→                results.push(OpResult {
166→                    name: member.name.clone(),
167→                    success: false,
168→                    message: format!("build failed: {}", e),
169→                    size: None,
170→                });
171→                if fail_fast {
172→                    return results;
173→                }
174→                continue;
175→            }
176→        };
177→
178→        // Find and run all test binaries in the target directory
179→        let profile = if release { "release" } else { "debug" };
180→        let target_dir = build_result.target_base.join(profile);
181→
182→        // Look for binaries that end with "-tests" in the target directory
183→        let test_binaries: Vec<_> = std::fs::read_dir(&target_dir)
184→            .into_iter()
185→            .flatten()
186→            .filter_map(|e| e.ok())
187→            .filter(|e| {
188→                let name = e.file_name().to_string_lossy().to_string();
189→                let is_file = e.path().is_file();
190→                let is_test = name.ends_with("-tests");
191→                let is_executable = e
192→                    .metadata()
193→                    .map(|m| m.permissions().mode() & 0o111 != 0)
194→                    .unwrap_or(false);
195→                is_file && is_test && is_executable
196→            })
197→            .collect();
198→
199→        if test_binaries.is_empty() {
200→            results.push(OpResult {
201→                name: member.name.clone(),
202→                success: false,
203→                message: "no test binaries found".to_string(),
204→                size: None,
205→            });
206→            continue;
207→        }
208→
209→        for entry in test_binaries {
210→            let path = entry.path();
211→            let bin_name = entry.file_name().to_string_lossy().to_string();
212→
213→            print!("  Running {}... ", bin_name);
214→
215→            let result = match run_binary(&path, &[]) {
216→                Ok(exit_code) => {
217→                    if exit_code == 0 {
218→                        println!("ok");
219→                        OpResult {
220→                            name: format!("{}/{}", member.name, bin_name),
221→                            success: true,
222→                            message: "ok".to_string(),
223→                            size: None,
224→                        }
225→                    } else {
226→                        println!("FAILED (exit {})", exit_code);
227→                        OpResult {
228→                            name: format!("{}/{}", member.name, bin_name),
229→                            success: false,
230→                            message: format!("exit code: {}", exit_code),
231→                            size: None,
232→                        }
233→                    }
234→                }
235→                Err(e) => {
236→                    println!("FAILED");
237→                    OpResult {
238→                        name: format!("{}/{}", member.name, bin_name),
239→                        success: false,
240→                        message: format!("run failed: {}", e),
241→                        size: None,
242→                    }
243→                }
244→            };
245→
246→            let failed = !result.success;
247→            results.push(result);
248→
249→            if failed && fail_fast {
250→                return results;
251→            }
252→        }
253→    }
254→
255→    results
256→}
257→
258→/// Print a summary of operation results (for tests)
259→pub fn print_test_summary(results: &[OpResult]) -> ExitCode {
260→    let max_name_len = results
261→        .iter()
262→        .map(|r| r.name.len())
263→        .max()
264→        .unwrap_or(5)
265→        .max(5);
266→
267→    println!();
268→    print_header!("TEST SUMMARY");
269→    println!("  {:width$}  Status", "Package", width = max_name_len);
270→
271→    let mut passed = 0;
272→    let mut failed = 0;
273→
274→    for result in results {
275→        let status = if result.success {
276→            passed += 1;
277→            "[PASS]"
278→        } else {
279→            failed += 1;
280→            "[FAIL]"
281→        };
282→        println!("  {:width$}  {status}", result.name, width = max_name_len);
283→    }
284→
285→    println!();
286→    println!("  Test: {} passed, {} failed", passed, failed);
287→    println!();
288→    println!("  Note: Run `tspec test -p tspec` or `cargo test -p tspec` to test tspec itself");
289→    print_hline!();
290→    println!();
291→
292→    if failed > 0 {
293→        ExitCode::from(1)
294→    } else {
295→        ExitCode::SUCCESS
296→    }
297→}
298→
299→/// Print a summary for build operations (OK/FAILED)
300→pub fn print_summary(results: &[OpResult]) -> ExitCode {
301→    let max_name_len = results
302→        .iter()
303→        .map(|r| r.name.len())
304→        .max()
305→        .unwrap_or(5)
306→        .max(5);
307→
308→    println!();
309→    print_header!("BUILD SUMMARY");
310→    println!(
311→        "  {:width$}  Status    Size",
312→        "Package",
313→        width = max_name_len
314→    );
315→
316→    let mut ok_count = 0;
317→    let mut failed_count = 0;
318→
319→    for result in results {
320→        let status = if result.success {
321→            ok_count += 1;
322→            "[ OK ]"
323→        } else {
324→            failed_count += 1;
325→            "[FAIL]"
326→        };
327→        let size_str = result
328→            .size
329→            .map(format_size)
330→            .unwrap_or_else(|| "--".to_string());
331→        println!(
332→            "  {:width$}  {status}  {:>6}",
333→            result.name,
334→            size_str,
335→            width = max_name_len
336→        );
337→    }
338→
339→    println!();
340→    println!("  Build: {} ok, {} failed", ok_count, failed_count);
341→    print_hline!();
342→    println!();
343→
344→    if failed_count > 0 {
345→        ExitCode::from(1)
346→    } else {
347→        ExitCode::SUCCESS
348→    }
349→}
350→
351→fn format_size(bytes: u64) -> String {
352→    if bytes >= 1_000_000 {
353→        format!("{:.1}M", bytes as f64 / 1_000_000.0)
354→    } else if bytes >= 1_000 {
355→        format!("{:.1}K", bytes as f64 / 1_000.0)
356→    } else {
357→        format!("{}", bytes)
358→    }
359→}
360→
361→/// Print a summary for run operations (shows exit codes, not pass/fail)
362→pub fn print_run_summary(results: &[OpResult]) -> ExitCode {
363→    let max_name_len = results
364→        .iter()
365→        .map(|r| r.name.len())
366→        .max()
367→        .unwrap_or(5)
368→        .max(5);
369→
370→    println!();
371→    print_header!("RUN SUMMARY");
372→    println!("  {:width$}  Exit", "Package", width = max_name_len);
373→
374→    let mut error_count = 0;
375→
376→    for result in results {
377→        if result.success {
378→            // Extract exit code number from message "exit code: X"
379→            let code = result
380→                .message
381→                .strip_prefix("exit code: ")
382→                .unwrap_or(&result.message);
383→            println!(
384→                "  {:width$}  {:>4}",
385→                result.name,
386→                code,
387→                width = max_name_len
388→            );
389→        } else {
390→            error_count += 1;
391→            println!(
392→                "  {:width$}  ERROR: {}",
393→                result.name,
394→                result.message,
395→                width = max_name_len
396→            );
397→        }
398→    }
399→
400→    println!();
401→    if error_count > 0 {
402→        println!("  Run: {} error(s)", error_count);
403→    }
404→    print_hline!();
405→    println!();
406→
407→    if error_count > 0 {
408→        ExitCode::from(1)
409→    } else {
410→        ExitCode::SUCCESS
411→    }
412→}
