use anyhow::Result;
use clap::Args;
use std::path::Path;
use std::process::ExitCode;

use super::{Execute, current_package_name, resolve_package_arg};
use crate::all::{compare_all, print_compare_summary};
use crate::compare::compare_specs;
use crate::find_paths::{find_tspecs, get_package_name, resolve_package_dir};
use crate::workspace::WorkspaceInfo;

/// Compare specs for a package (size only)
#[derive(Args)]
pub struct CompareCmd {
    /// Package to compare (name or path, e.g. "." for current dir)
    #[arg(value_name = "PACKAGE")]
    pub positional: Option<String>,
    /// Package to compare (defaults to current directory)
    #[arg(short = 'p', long = "package")]
    pub package: Option<String>,
    /// Compare all workspace packages (even when in a package directory)
    #[arg(short = 'w', long = "workspace")]
    pub workspace: bool,
    /// Spec file(s) or glob pattern(s) (defaults to tspec* pattern)
    #[arg(short = 't', long = "tspec", num_args = 1..)]
    pub tspec: Vec<String>,
    /// Stop on first failure (for all-packages mode)
    #[arg(short, long)]
    pub fail_fast: bool,
}

impl Execute for CompareCmd {
    fn execute(&self, project_root: &Path) -> Result<ExitCode> {
        // Resolve package: --workspace > -p/positional PKG > cwd > all
        let resolved = if self.workspace {
            None
        } else {
            match self.positional.as_deref().or(self.package.as_deref()) {
                Some(pkg) => resolve_package_arg(pkg)?,
                None => current_package_name(),
            }
        };

        match resolved {
            None => {
                if !self.tspec.is_empty() {
                    anyhow::bail!(
                        "-t/--tspec cannot be used in all-packages mode. Each package uses its own tspecs."
                    );
                }
                let workspace = WorkspaceInfo::discover()?;
                let results = compare_all(&workspace, self.fail_fast);
                Ok(print_compare_summary(&results))
            }
            Some(pkg_name) => {
                let package_dir = resolve_package_dir(project_root, Some(&pkg_name))?;
                let pkg_name = get_package_name(&package_dir)?;
                let spec_paths = if self.tspec.is_empty() {
                    find_tspecs(&package_dir, &self.tspec).unwrap_or_default()
                } else {
                    find_tspecs(&package_dir, &self.tspec)?
                };
                compare_specs(&pkg_name, &spec_paths)?;
                Ok(ExitCode::SUCCESS)
            }
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use crate::cli::Cli;
    use clap::Parser;

    fn parse(args: &[&str]) -> CompareCmd {
        let mut full = vec!["tspec", "compare"];
        full.extend_from_slice(args);
        let cli = Cli::try_parse_from(full).unwrap();
        match cli.command {
            crate::cli::Commands::Compare(cmd) => cmd,
            _ => panic!("expected Compare command"),
        }
    }

    #[test]
    fn package_optional() {
        let cmd = parse(&[]);
        assert!(cmd.package.is_none());
    }

    #[test]
    fn package_explicit() {
        let cmd = parse(&["-p", "myapp"]);
        assert_eq!(cmd.package.as_deref(), Some("myapp"));
    }

    #[test]
    fn tspec_empty_by_default() {
        let cmd = parse(&[]);
        assert!(cmd.tspec.is_empty());
    }

    #[test]
    fn tspec_single_file() {
        let cmd = parse(&["-t", "foo.ts.toml"]);
        assert_eq!(cmd.tspec, vec!["foo.ts.toml"]);
    }

    #[test]
    fn tspec_multiple_values_one_flag() {
        // Simulates shell-expanded glob: -t file1 file2
        let cmd = parse(&["-t", "a.ts.toml", "b.ts.toml"]);
        assert_eq!(cmd.tspec, vec!["a.ts.toml", "b.ts.toml"]);
    }

    #[test]
    fn tspec_multiple_flags() {
        let cmd = parse(&["-t", "a.ts.toml", "-t", "b.ts.toml"]);
        assert_eq!(cmd.tspec, vec!["a.ts.toml", "b.ts.toml"]);
    }

    #[test]
    fn all_flags_together() {
        let cmd = parse(&["-p", "myapp", "-t", "spec.ts.toml"]);
        assert_eq!(cmd.package.as_deref(), Some("myapp"));
        assert_eq!(cmd.tspec, vec!["spec.ts.toml"]);
    }

    #[test]
    fn workspace_flag_short() {
        let cmd = parse(&["-w"]);
        assert!(cmd.workspace);
    }

    #[test]
    fn workspace_flag_long() {
        let cmd = parse(&["--workspace"]);
        assert!(cmd.workspace);
    }

    #[test]
    fn workspace_default_false() {
        let cmd = parse(&[]);
        assert!(!cmd.workspace);
    }

    #[test]
    fn fail_fast_flag() {
        let cmd = parse(&["-w", "-f"]);
        assert!(cmd.workspace);
        assert!(cmd.fail_fast);
    }

    #[test]
    fn fail_fast_long() {
        let cmd = parse(&["--workspace", "--fail-fast"]);
        assert!(cmd.workspace);
        assert!(cmd.fail_fast);
    }
}

//! Batch operations on all workspace packages
//!
//! Provides build_all, run_all, test_all for operating on all workspace members.

use std::os::unix::fs::PermissionsExt;
use std::process::ExitCode;

use crate::binary::{binary_size, strip_binary};
use crate::cargo_build::build_package;
use crate::compare::compare_specs;
use crate::find_paths::find_tspecs;
use crate::run::run_binary;
use crate::testing::test_package;
use crate::workspace::{PackageKind, WorkspaceInfo};
use crate::{print_header, print_hline};

/// Result of a batch operation on a single package
pub struct OpResult {
    pub name: String,
    pub success: bool,
    pub message: String,
    pub size: Option<u64>,
}

/// Build all workspace packages (excluding build tools)
pub fn build_all(
    workspace: &WorkspaceInfo,
    tspec: Option<&str>,
    release: bool,
    strip: bool,
    fail_fast: bool,
) -> Vec<OpResult> {
    let mut results = Vec::new();

    for member in workspace.buildable_members() {
        println!("=== {} ===", member.name);

        let result = match build_package(&member.name, tspec, release) {
            Ok(build_result) => {
                if strip
                    && member.has_binary
                    && let Err(e) = strip_binary(&build_result.binary_path)
                {
                    eprintln!("  warning: strip failed: {}", e);
                }
                let size = binary_size(&build_result.binary_path).ok();
                OpResult {
                    name: member.name.clone(),
                    success: true,
                    message: format!("{}", build_result.binary_path.display()),
                    size,
                }
            }
            Err(e) => OpResult {
                name: member.name.clone(),
                success: false,
                message: e.to_string(),
                size: None,
            },
        };

        let failed = !result.success;
        results.push(result);

        if failed && fail_fast {
            break;
        }
    }

    results
}

/// Run all app packages sequentially
pub fn run_all(
    workspace: &WorkspaceInfo,
    tspec: Option<&str>,
    release: bool,
    strip: bool,
) -> Vec<OpResult> {
    let mut results = Vec::new();

    for member in workspace.runnable_members() {
        println!("=== {} ===", member.name);

        let result = match build_package(&member.name, tspec, release) {
            Ok(build_result) => {
                if strip && let Err(e) = strip_binary(&build_result.binary_path) {
                    eprintln!("  warning: strip failed: {}", e);
                }
                match run_binary(&build_result.binary_path, &[]) {
                    Ok(exit_code) => OpResult {
                        name: member.name.clone(),
                        success: true, // We don't treat non-zero exit as failure
                        message: format!("exit code: {}", exit_code),
                        size: None,
                    },
                    Err(e) => OpResult {
                        name: member.name.clone(),
                        success: false,
                        message: format!("run failed: {}", e),
                        size: None,
                    },
                }
            }
            Err(e) => OpResult {
                name: member.name.clone(),
                success: false,
                message: format!("build failed: {}", e),
                size: None,
            },
        };

        results.push(result);
    }

    results
}

/// Test all workspace packages
pub fn test_all(
    workspace: &WorkspaceInfo,
    tspec: Option<&str>,
    release: bool,
    fail_fast: bool,
) -> Vec<OpResult> {
    let mut results = Vec::new();

    // Test regular packages (excluding Test kind which needs special handling)
    for member in workspace.buildable_members() {
        if member.kind == PackageKind::Test {
            continue; // Handle test packages separately
        }

        println!("=== {} ===", member.name);

        let result = match test_package(&member.name, tspec, release) {
            Ok(()) => OpResult {
                name: member.name.clone(),
                success: true,
                message: "ok".to_string(),
                size: None,
            },
            Err(e) => OpResult {
                name: member.name.clone(),
                success: false,
                message: e.to_string(),
                size: None,
            },
        };

        let failed = !result.success;
        results.push(result);

        if failed && fail_fast {
            return results;
        }
    }

    // Handle test packages (like rlibc-x2-tests) - build and run all test binaries
    for member in workspace.test_members() {
        println!("=== {} ===", member.name);

        // Build the test package (builds all binaries)
        let build_result = match build_package(&member.name, tspec, release) {
            Ok(r) => r,
            Err(e) => {
                results.push(OpResult {
                    name: member.name.clone(),
                    success: false,
                    message: format!("build failed: {}", e),
                    size: None,
                });
                if fail_fast {
                    return results;
                }
                continue;
            }
        };

        // Find and run all test binaries in the target directory
        let profile = if release { "release" } else { "debug" };
        let target_dir = build_result.target_base.join(profile);

        // Look for binaries that end with "-tests" in the target directory
        let test_binaries: Vec<_> = std::fs::read_dir(&target_dir)
            .into_iter()
            .flatten()
            .filter_map(|e| e.ok())
            .filter(|e| {
                let name = e.file_name().to_string_lossy().to_string();
                let is_file = e.path().is_file();
                let is_test = name.ends_with("-tests");
                let is_executable = e
                    .metadata()
                    .map(|m| m.permissions().mode() & 0o111 != 0)
                    .unwrap_or(false);
                is_file && is_test && is_executable
            })
            .collect();

        if test_binaries.is_empty() {
            results.push(OpResult {
                name: member.name.clone(),
                success: false,
                message: "no test binaries found".to_string(),
                size: None,
            });
            continue;
        }

        for entry in test_binaries {
            let path = entry.path();
            let bin_name = entry.file_name().to_string_lossy().to_string();

            print!("  Running {}... ", bin_name);

            let result = match run_binary(&path, &[]) {
                Ok(exit_code) => {
                    if exit_code == 0 {
                        println!("ok");
                        OpResult {
                            name: format!("{}/{}", member.name, bin_name),
                            success: true,
                            message: "ok".to_string(),
                            size: None,
                        }
                    } else {
                        println!("FAILED (exit {})", exit_code);
                        OpResult {
                            name: format!("{}/{}", member.name, bin_name),
                            success: false,
                            message: format!("exit code: {}", exit_code),
                            size: None,
                        }
                    }
                }
                Err(e) => {
                    println!("FAILED");
                    OpResult {
                        name: format!("{}/{}", member.name, bin_name),
                        success: false,
                        message: format!("run failed: {}", e),
                        size: None,
                    }
                }
            };

            let failed = !result.success;
            results.push(result);

            if failed && fail_fast {
                return results;
            }
        }
    }

    results
}

/// Print a summary of operation results (for tests)
pub fn print_test_summary(results: &[OpResult]) -> ExitCode {
    let max_name_len = results
        .iter()
        .map(|r| r.name.len())
        .max()
        .unwrap_or(5)
        .max(5);

    println!();
    print_header!("TEST SUMMARY");
    println!("  {:width$}  Status", "Package", width = max_name_len);

    let mut passed = 0;
    let mut failed = 0;

    for result in results {
        let status = if result.success {
            passed += 1;
            "[PASS]"
        } else {
            failed += 1;
            "[FAIL]"
        };
        println!("  {:width$}  {status}", result.name, width = max_name_len);
    }

    println!();
    println!("  Test: {} passed, {} failed", passed, failed);
    println!();
    println!("  Note: Run `tspec test -p tspec` or `cargo test -p tspec` to test tspec itself");
    print_hline!();
    println!();

    if failed > 0 {
        ExitCode::from(1)
    } else {
        ExitCode::SUCCESS
    }
}

/// Print a summary for build operations (OK/FAILED)
pub fn print_summary(results: &[OpResult]) -> ExitCode {
    let max_name_len = results
        .iter()
        .map(|r| r.name.len())
        .max()
        .unwrap_or(5)
        .max(5);

    println!();
    print_header!("BUILD SUMMARY");
    println!(
        "  {:width$}  Status    Size",
        "Package",
        width = max_name_len
    );

    let mut ok_count = 0;
    let mut failed_count = 0;

    for result in results {
        let status = if result.success {
            ok_count += 1;
            "[ OK ]"
        } else {
            failed_count += 1;
            "[FAIL]"
        };
        let size_str = result
            .size
            .map(format_size)
            .unwrap_or_else(|| "--".to_string());
        println!(
            "  {:width$}  {status}  {:>6}",
            result.name,
            size_str,
            width = max_name_len
        );
    }

    println!();
    println!("  Build: {} ok, {} failed", ok_count, failed_count);
    print_hline!();
    println!();

    if failed_count > 0 {
        ExitCode::from(1)
    } else {
        ExitCode::SUCCESS
    }
}

fn format_size(bytes: u64) -> String {
    if bytes >= 1_000_000 {
        format!("{:.1}M", bytes as f64 / 1_000_000.0)
    } else if bytes >= 1_000 {
        format!("{:.1}K", bytes as f64 / 1_000.0)
    } else {
        format!("{}", bytes)
    }
}

/// Compare all workspace packages that have binaries
pub fn compare_all(workspace: &WorkspaceInfo, fail_fast: bool) -> Vec<OpResult> {
    let mut results = Vec::new();

    for member in workspace.buildable_members() {
        if !member.has_binary {
            continue;
        }

        let spec_paths = find_tspecs(&member.path, &[]).unwrap_or_default();

        println!("=== {} ===", member.name);

        let result = match compare_specs(&member.name, &spec_paths) {
            Ok(()) => OpResult {
                name: member.name.clone(),
                success: true,
                message: "ok".to_string(),
                size: None,
            },
            Err(e) => OpResult {
                name: member.name.clone(),
                success: false,
                message: e.to_string(),
                size: None,
            },
        };

        let failed = !result.success;
        results.push(result);

        if failed && fail_fast {
            break;
        }
    }

    results
}

/// Print a summary for compare operations
pub fn print_compare_summary(results: &[OpResult]) -> ExitCode {
    let max_name_len = results
        .iter()
        .map(|r| r.name.len())
        .max()
        .unwrap_or(5)
        .max(5);

    println!();
    print_header!("COMPARE SUMMARY");
    println!("  {:width$}  Status", "Package", width = max_name_len);

    let mut ok_count = 0;
    let mut failed_count = 0;

    for result in results {
        let status = if result.success {
            ok_count += 1;
            "[ OK ]"
        } else {
            failed_count += 1;
            "[FAIL]"
        };
        println!("  {:width$}  {status}", result.name, width = max_name_len);
    }

    println!();
    println!("  Compare: {} ok, {} failed", ok_count, failed_count);
    print_hline!();
    println!();

    if failed_count > 0 {
        ExitCode::from(1)
    } else {
        ExitCode::SUCCESS
    }
}

/// Print a summary for run operations (shows exit codes, not pass/fail)
pub fn print_run_summary(results: &[OpResult]) -> ExitCode {
    let max_name_len = results
        .iter()
        .map(|r| r.name.len())
        .max()
        .unwrap_or(5)
        .max(5);

    println!();
    print_header!("RUN SUMMARY");
    println!("  {:width$}  Exit", "Package", width = max_name_len);

    let mut error_count = 0;

    for result in results {
        if result.success {
            // Extract exit code number from message "exit code: X"
            let code = result
                .message
                .strip_prefix("exit code: ")
                .unwrap_or(&result.message);
            println!(
                "  {:width$}  {:>4}",
                result.name,
                code,
                width = max_name_len
            );
        } else {
            error_count += 1;
            println!(
                "  {:width$}  ERROR: {}",
                result.name,
                result.message,
                width = max_name_len
            );
        }
    }

    println!();
    if error_count > 0 {
        println!("  Run: {} error(s)", error_count);
    }
    print_hline!();
    println!();

    if error_count > 0 {
        ExitCode::from(1)
    } else {
        ExitCode::SUCCESS
    }
}

