  1→//! Batch operations on all workspace packages
  2→//!
  3→//! Provides build_all, run_all, test_all for operating on all workspace members.
  4→
  5→use std::os::unix::fs::PermissionsExt;
  6→use std::process::ExitCode;
  7→
  8→use std::path::{Path, PathBuf};
  9→
 10→use crate::binary::{binary_size, strip_binary};
 11→use crate::cargo_build::{build_package, test_package};
 12→use crate::compare::{SpecResult, compare_specs, print_comparison};
 13→use crate::find_paths::find_tspecs;
 14→use crate::run::run_binary;
 15→use crate::tspec::spec_name_from_path;
 16→use crate::types::CargoFlags;
 17→use crate::workspace::{PackageKind, WorkspaceInfo};
 18→use crate::{print_header, print_hline};
 19→
 20→/// Normalize tspec patterns for per-package matching in all-packages mode.
 21→///
 22→/// Strips directory components so shell-expanded paths from the workspace root
 23→/// don't leak into sub-packages. Filters out non-tspec shell expansions
 24→/// (e.g. `target/`, `tools/`) — only `.ts.toml` files and glob patterns are kept.
 25→///
 26→/// Returns `None` if all patterns were filtered out (likely shell expansion of a
 27→/// glob that matched non-tspec entries). The caller should warn about quoting.
 28→fn normalize_tspec_patterns(patterns: &[String]) -> Option<Vec<String>> {
 29→    if patterns.is_empty() {
 30→        return Some(Vec::new());
 31→    }
 32→    let filenames: Vec<String> = patterns
 33→        .iter()
 34→        .filter_map(|p| {
 35→            let name = Path::new(p)
 36→                .file_name()
 37→                .map(|f| f.to_string_lossy().into_owned())
 38→                .unwrap_or_else(|| p.clone());
 39→            // Keep glob patterns (unexpanded) and actual tspec files
 40→            let is_glob = name.contains('*') || name.contains('?') || name.contains('[');
 41→            let is_tspec = name.ends_with(crate::TSPEC_SUFFIX);
 42→            if is_glob || is_tspec {
 43→                Some(name)
 44→            } else {
 45→                None
 46→            }
 47→        })
 48→        .collect();
 49→    if filenames.is_empty() && !patterns.is_empty() {
 50→        None // all patterns were filtered — likely shell expansion
 51→    } else {
 52→        Some(filenames)
 53→    }
 54→}
 55→
 56→/// Warn that shell glob expansion likely ate the tspec pattern.
 57→pub fn warn_shell_glob_expansion(patterns: &[String]) {
 58→    eprintln!(
 59→        "Warning: -t arguments ({}) don't look like tspec files.",
 60→        patterns.join(", ")
 61→    );
 62→    eprintln!("  The shell likely expanded your glob before tspec could see it.");
 63→    eprintln!("  Quote the pattern to prevent shell expansion: -t 'pattern*'");
 64→}
 65→
 66→/// Resolve tspec patterns for a workspace member.
 67→///
 68→/// Returns the matching spec paths, or an empty vec if no patterns match.
 69→/// When `patterns` is empty, returns an empty vec (caller should use default behavior).
 70→fn resolve_specs_for_member(member_path: &Path, patterns: &[String]) -> Vec<PathBuf> {
 71→    if patterns.is_empty() {
 72→        return Vec::new();
 73→    }
 74→    find_tspecs(member_path, patterns).unwrap_or_default()
 75→}
 76→
 77→/// Extract a short spec label from an optional tspec path.
 78→fn spec_label(tspec: &Option<String>) -> String {
 79→    match tspec {
 80→        Some(path) => spec_name_from_path(Path::new(path)),
 81→        None => String::new(),
 82→    }
 83→}
 84→
 85→/// Result of a batch operation on a single package
 86→pub struct OpResult {
 87→    pub name: String,
 88→    pub spec: String,
 89→    pub success: bool,
 90→    pub message: String,
 91→    pub size: Option<u64>,
 92→}
 93→
 94→/// Build all workspace packages (excluding build tools)
 95→///
 96→/// When `tspec_patterns` is empty, each package uses its default spec.
 97→/// When non-empty, patterns are resolved per-package; packages with no matches are skipped.
 98→pub fn build_all(
 99→    workspace: &WorkspaceInfo,
100→    tspec_patterns: &[String],
101→    cli_profile: Option<&str>,
102→    strip: bool,
103→    fail_fast: bool,
104→    flags: &CargoFlags,
105→) -> Vec<OpResult> {
106→    let normalized = match normalize_tspec_patterns(tspec_patterns) {
107→        Some(n) => n,
108→        None => {
109→            warn_shell_glob_expansion(tspec_patterns);
110→            return Vec::new();
111→        }
112→    };
113→    let mut results = Vec::new();
114→
115→    for member in workspace.buildable_members() {
116→        let specs = resolve_specs_for_member(&member.path, &normalized);
117→        if specs.is_empty() && !normalized.is_empty() {
118→            continue;
119→        }
120→
121→        println!("=== {} ===", member.name);
122→
123→        let tspec_list: Vec<Option<String>> = if specs.is_empty() {
124→            vec![None]
125→        } else {
126→            specs
127→                .into_iter()
128→                .map(|p| Some(p.to_string_lossy().into_owned()))
129→                .collect()
130→        };
131→
132→        for tspec in &tspec_list {
133→            let spec = spec_label(tspec);
134→            let result = match build_package(&member.name, tspec.as_deref(), cli_profile, flags) {
135→                Ok(build_result) => {
136→                    if strip
137→                        && member.has_binary
138→                        && let Err(e) = strip_binary(&build_result.binary_path)
139→                    {
140→                        eprintln!("  warning: strip failed: {}", e);
141→                    }
142→                    let size = binary_size(&build_result.binary_path).ok();
143→                    OpResult {
144→                        name: member.name.clone(),
145→                        spec,
146→                        success: true,
147→                        message: format!("{}", build_result.binary_path.display()),
148→                        size,
149→                    }
150→                }
151→                Err(e) => OpResult {
152→                    name: member.name.clone(),
153→                    spec,
154→                    success: false,
155→                    message: e.to_string(),
156→                    size: None,
157→                },
158→            };
159→
160→            let failed = !result.success;
161→            results.push(result);
162→
163→            if failed && fail_fast {
164→                return results;
165→            }
166→        }
167→    }
168→
169→    results
170→}
171→
172→/// Run all app packages sequentially
173→///
174→/// When `tspec_patterns` is empty, each package uses its default spec.
175→/// When non-empty, patterns are resolved per-package; packages with no matches are skipped.
176→pub fn run_all(
177→    workspace: &WorkspaceInfo,
178→    tspec_patterns: &[String],
179→    cli_profile: Option<&str>,
180→    strip: bool,
181→    flags: &CargoFlags,
182→) -> Vec<OpResult> {
183→    let normalized = match normalize_tspec_patterns(tspec_patterns) {
184→        Some(n) => n,
185→        None => {
186→            warn_shell_glob_expansion(tspec_patterns);
187→            return Vec::new();
188→        }
189→    };
190→    let mut results = Vec::new();
191→
192→    for member in workspace.runnable_members() {
193→        let specs = resolve_specs_for_member(&member.path, &normalized);
194→        if specs.is_empty() && !normalized.is_empty() {
195→            continue;
196→        }
197→
198→        println!("=== {} ===", member.name);
199→
200→        let tspec_list: Vec<Option<String>> = if specs.is_empty() {
201→            vec![None]
202→        } else {
203→            specs
204→                .into_iter()
205→                .map(|p| Some(p.to_string_lossy().into_owned()))
206→                .collect()
207→        };
208→
209→        for tspec in &tspec_list {
210→            let spec = spec_label(tspec);
211→            let result = match build_package(&member.name, tspec.as_deref(), cli_profile, flags) {
212→                Ok(build_result) => {
213→                    if strip && let Err(e) = strip_binary(&build_result.binary_path) {
214→                        eprintln!("  warning: strip failed: {}", e);
215→                    }
216→                    match run_binary(&build_result.binary_path, &[]) {
217→                        Ok(exit_code) => OpResult {
218→                            name: member.name.clone(),
219→                            spec: spec.clone(),
220→                            success: true,
221→                            message: format!("exit code: {}", exit_code),
222→                            size: None,
223→                        },
224→                        Err(e) => OpResult {
225→                            name: member.name.clone(),
226→                            spec: spec.clone(),
227→                            success: false,
228→                            message: format!("run failed: {}", e),
229→                            size: None,
230→                        },
231→                    }
232→                }
233→                Err(e) => OpResult {
234→                    name: member.name.clone(),
235→                    spec,
236→                    success: false,
237→                    message: format!("build failed: {}", e),
238→                    size: None,
239→                },
240→            };
241→
242→            results.push(result);
243→        }
244→    }
245→
246→    results
247→}
248→
249→/// Test all workspace packages
250→///
251→/// When `tspec_patterns` is empty, each package uses its default spec.
252→/// When non-empty, patterns are resolved per-package; packages with no matches are skipped.
253→pub fn test_all(
254→    workspace: &WorkspaceInfo,
255→    tspec_patterns: &[String],
256→    cli_profile: Option<&str>,
257→    fail_fast: bool,
258→    flags: &CargoFlags,
259→) -> Vec<OpResult> {
260→    let normalized = match normalize_tspec_patterns(tspec_patterns) {
261→        Some(n) => n,
262→        None => {
263→            warn_shell_glob_expansion(tspec_patterns);
264→            return Vec::new();
265→        }
266→    };
267→    let mut results = Vec::new();
268→
269→    // Test regular packages (excluding Test kind which needs special handling)
270→    for member in workspace.buildable_members() {
271→        if member.kind == PackageKind::Test {
272→            continue; // Handle test packages separately
273→        }
274→
275→        let specs = resolve_specs_for_member(&member.path, &normalized);
276→        if specs.is_empty() && !normalized.is_empty() {
277→            continue;
278→        }
279→
280→        println!("=== {} ===", member.name);
281→
282→        let tspec_list: Vec<Option<String>> = if specs.is_empty() {
283→            vec![None]
284→        } else {
285→            specs
286→                .into_iter()
287→                .map(|p| Some(p.to_string_lossy().into_owned()))
288→                .collect()
289→        };
290→
291→        for tspec in &tspec_list {
292→            let spec = spec_label(tspec);
293→            let result = match test_package(&member.name, tspec.as_deref(), cli_profile, flags) {
294→                Ok(()) => OpResult {
295→                    name: member.name.clone(),
296→                    spec,
297→                    success: true,
298→                    message: "ok".to_string(),
299→                    size: None,
300→                },
301→                Err(e) => OpResult {
302→                    name: member.name.clone(),
303→                    spec,
304→                    success: false,
305→                    message: e.to_string(),
306→                    size: None,
307→                },
308→            };
309→
310→            let failed = !result.success;
311→            results.push(result);
312→
313→            if failed && fail_fast {
314→                return results;
315→            }
316→        }
317→    }
318→
319→    // Handle test packages (like rlibc-x2-tests) - build and run all test binaries
320→    for member in workspace.test_members() {
321→        let specs = resolve_specs_for_member(&member.path, &normalized);
322→        if specs.is_empty() && !normalized.is_empty() {
323→            continue;
324→        }
325→
326→        println!("=== {} ===", member.name);
327→
328→        // Build the test package (builds all binaries)
329→        let build_tspec = specs.first().map(|p| p.to_string_lossy().into_owned());
330→        let spec = spec_label(&build_tspec);
331→        let build_result =
332→            match build_package(&member.name, build_tspec.as_deref(), cli_profile, flags) {
333→                Ok(r) => r,
334→                Err(e) => {
335→                    results.push(OpResult {
336→                        name: member.name.clone(),
337→                        spec,
338→                        success: false,
339→                        message: format!("build failed: {}", e),
340→                        size: None,
341→                    });
342→                    if fail_fast {
343→                        return results;
344→                    }
345→                    continue;
346→                }
347→            };
348→
349→        // Find and run all test binaries in the target directory
350→        let profile_dir = crate::types::profile_dir_name(cli_profile.unwrap_or("debug"));
351→        let target_dir = build_result.target_base.join(profile_dir);
352→
353→        // Look for binaries that end with "-tests" in the target directory
354→        let test_binaries: Vec<_> = std::fs::read_dir(&target_dir)
355→            .into_iter()
356→            .flatten()
357→            .filter_map(|e| e.ok())
358→            .filter(|e| {
359→                let name = e.file_name().to_string_lossy().to_string();
360→                let is_file = e.path().is_file();
361→                let is_test = name.ends_with("-tests");
362→                let is_executable = e
363→                    .metadata()
364→                    .map(|m| m.permissions().mode() & 0o111 != 0)
365→                    .unwrap_or(false);
366→                is_file && is_test && is_executable
367→            })
368→            .collect();
369→
370→        if test_binaries.is_empty() {
371→            results.push(OpResult {
372→                name: member.name.clone(),
373→                spec: spec.clone(),
374→                success: false,
375→                message: "no test binaries found".to_string(),
376→                size: None,
377→            });
378→            continue;
379→        }
380→
381→        for entry in test_binaries {
382→            let path = entry.path();
383→            let bin_name = entry.file_name().to_string_lossy().to_string();
384→
385→            print!("  Running {}... ", bin_name);
386→
387→            let result = match run_binary(&path, &[]) {
388→                Ok(exit_code) => {
389→                    if exit_code == 0 {
390→                        println!("ok");
391→                        OpResult {
392→                            name: format!("{}/{}", member.name, bin_name),
393→                            spec: spec.clone(),
394→                            success: true,
395→                            message: "ok".to_string(),
396→                            size: None,
397→                        }
398→                    } else {
399→                        println!("FAILED (exit {})", exit_code);
400→                        OpResult {
401→                            name: format!("{}/{}", member.name, bin_name),
402→                            spec: spec.clone(),
403→                            success: false,
404→                            message: format!("exit code: {}", exit_code),
405→                            size: None,
406→                        }
407→                    }
408→                }
409→                Err(e) => {
410→                    println!("FAILED");
411→                    OpResult {
412→                        name: format!("{}/{}", member.name, bin_name),
413→                        spec: spec.clone(),
414→                        success: false,
415→                        message: format!("run failed: {}", e),
416→                        size: None,
417→                    }
418→                }
419→            };
420→
421→            let failed = !result.success;
422→            results.push(result);
423→
424→            if failed && fail_fast {
425→                return results;
426→            }
427→        }
428→    }
429→
430→    results
431→}
