433→/// A row for the summary table: package name, spec, and pre-formatted detail string.
434→struct SummaryRow {
435→    name: String,
436→    spec: String,
437→    detail: String,
438→}
439→
440→/// Print a summary table with consistent formatting across all operations.
441→///
442→/// - `ws_name` — workspace/package name for the header
443→/// - `cmd` — operation name ("TEST", "BUILD", "RUN", "COMPARE")
444→/// - `detail_header` — column header for the detail column (e.g. "Status", "Status    Size", "Exit")
445→/// - `rows` — pre-formatted rows
446→/// - `footer` — footer line (e.g. "Test: 3 passed, 1 failed")
447→fn print_summary_table(
448→    ws_name: &str,
449→    cmd: &str,
450→    detail_header: &str,
451→    rows: &[SummaryRow],
452→    footer: &str,
453→) {
454→    let max_name_len = rows.iter().map(|r| r.name.len()).max().unwrap_or(7).max(7);
455→    let has_spec = rows.iter().any(|r| !r.spec.is_empty());
456→    let max_spec_len = if has_spec {
457→        rows.iter().map(|r| r.spec.len()).max().unwrap_or(4).max(4)
458→    } else {
459→        0
460→    };
461→
462→    println!();
463→    print_header!(format!("{ws_name} {cmd} SUMMARY"));
464→    if has_spec {
465→        println!(
466→            "  {:nw$}  {:sw$}  {detail_header}",
467→            "Package",
468→            "Spec",
469→            nw = max_name_len,
470→            sw = max_spec_len
471→        );
472→    } else {
473→        println!(
474→            "  {:width$}  {detail_header}",
475→            "Package",
476→            width = max_name_len
477→        );
478→    }
479→
480→    for row in rows {
481→        if has_spec {
482→            println!(
483→                "  {:nw$}  {:sw$}  {}",
484→                row.name,
485→                row.spec,
486→                row.detail,
487→                nw = max_name_len,
488→                sw = max_spec_len
489→            );
490→        } else {
491→            println!(
492→                "  {:width$}  {}",
493→                row.name,
494→                row.detail,
495→                width = max_name_len
496→            );
497→        }
498→    }
499→
500→    println!();
501→    if !footer.is_empty() {
502→        println!("  {footer}");
503→    }
504→    print_hline!();
505→    println!();
506→}
