508→/// Print a summary of operation results (for tests)
509→pub fn print_test_summary(name: &str, results: &[OpResult]) -> ExitCode {
510→    let mut passed = 0;
511→    let mut failed = 0;
512→
513→    let rows: Vec<SummaryRow> = results
514→        .iter()
515→        .map(|r| {
516→            let detail = if r.success {
517→                passed += 1;
518→                "[PASS]".to_string()
519→            } else {
520→                failed += 1;
521→                "[FAIL]".to_string()
522→            };
523→            SummaryRow {
524→                name: r.name.clone(),
525→                spec: r.spec.clone(),
526→                detail,
527→            }
528→        })
529→        .collect();
530→
531→    print_summary_table(
532→        name,
533→        "TEST",
534→        "Status",
535→        &rows,
536→        &format!("Test: {passed} passed, {failed} failed"),
537→    );
538→
539→    if failed > 0 {
540→        ExitCode::from(1)
541→    } else {
542→        ExitCode::SUCCESS
543→    }
544→}
545→
546→/// Print a summary for build operations (OK/FAILED)
547→pub fn print_summary(name: &str, results: &[OpResult]) -> ExitCode {
548→    let mut ok_count = 0;
549→    let mut failed_count = 0;
550→
551→    let rows: Vec<SummaryRow> = results
552→        .iter()
553→        .map(|r| {
554→            let status = if r.success {
555→                ok_count += 1;
556→                "[ OK ]"
557→            } else {
558→                failed_count += 1;
559→                "[FAIL]"
560→            };
561→            let size_str = r.size.map(format_size).unwrap_or_else(|| "--".to_string());
562→            SummaryRow {
563→                name: r.name.clone(),
564→                spec: r.spec.clone(),
565→                detail: format!("{status}  {size_str:>6}"),
566→            }
567→        })
568→        .collect();
569→
570→    print_summary_table(
571→        name,
572→        "BUILD",
573→        "Status    Size",
574→        &rows,
575→        &format!("Build: {ok_count} ok, {failed_count} failed"),
576→    );
577→
578→    if failed_count > 0 {
579→        ExitCode::from(1)
580→    } else {
581→        ExitCode::SUCCESS
582→    }
583→}
584→
585→fn format_size(bytes: u64) -> String {
586→    if bytes >= 1_000_000 {
587→        format!("{:.1}M", bytes as f64 / 1_000_000.0)
588→    } else if bytes >= 1_000 {
589→        format!("{:.1}K", bytes as f64 / 1_000.0)
590→    } else {
591→        format!("{}", bytes)
592→    }
593→}
594→
595→/// Result of a compare operation on a single package
596→pub struct CompareResult {
597→    pub op: OpResult,
598→    pub specs: Vec<SpecResult>,
599→}
600→
601→/// Compare all workspace packages that have binaries
602→///
603→/// When `tspec_patterns` is empty, each package discovers its own specs via default glob.
604→/// When non-empty, patterns are resolved per-package; packages with no matches are skipped.
605→pub fn compare_all(
606→    workspace: &WorkspaceInfo,
607→    tspec_patterns: &[String],
608→    fail_fast: bool,
609→    flags: &CargoFlags,
610→) -> Vec<CompareResult> {
611→    let normalized = match normalize_tspec_patterns(tspec_patterns) {
612→        Some(n) => n,
613→        None => {
614→            warn_shell_glob_expansion(tspec_patterns);
615→            return Vec::new();
616→        }
617→    };
618→    let mut results = Vec::new();
619→
620→    for member in workspace.buildable_members() {
621→        if !member.has_binary {
622→            continue;
623→        }
624→
625→        let spec_paths = if normalized.is_empty() {
626→            find_tspecs(&member.path, &[]).unwrap_or_default()
627→        } else {
628→            let resolved = resolve_specs_for_member(&member.path, &normalized);
629→            if resolved.is_empty() {
630→                continue;
631→            }
632→            resolved
633→        };
634→
635→        println!("=== {} ===", member.name);
636→
637→        let (op, specs) = match compare_specs(&member.name, &spec_paths, flags) {
638→            Ok(spec_results) => (
639→                OpResult {
640→                    name: member.name.clone(),
641→                    spec: String::new(),
642→                    success: true,
643→                    message: "ok".to_string(),
644→                    size: None,
645→                },
646→                spec_results,
647→            ),
648→            Err(e) => (
649→                OpResult {
650→                    name: member.name.clone(),
651→                    spec: String::new(),
652→                    success: false,
653→                    message: e.to_string(),
654→                    size: None,
655→                },
656→                Vec::new(),
657→            ),
658→        };
659→
660→        let failed = !op.success;
661→        results.push(CompareResult { op, specs });
662→
663→        if failed && fail_fast {
664→            break;
665→        }
666→    }
667→
668→    results
669→}
670→
671→/// Print a summary for compare operations
672→///
673→/// With a single package, just prints its comparison table.
674→/// With multiple packages, reprints all per-package tables then an overall OK/FAIL summary.
675→pub fn print_compare_summary(name: &str, results: &[CompareResult]) -> ExitCode {
676→    let has_failure = results.iter().any(|r| !r.op.success);
677→
678→    // Reprint per-package comparison tables together
679→    for result in results {
680→        if !result.specs.is_empty() {
681→            print_comparison(&result.op.name, &result.specs);
682→        }
683→    }
684→
685→    // Only show overall COMPARE SUMMARY when there are multiple packages
686→    if results.len() > 1 {
687→        let mut ok_count = 0;
688→        let mut failed_count = 0;
689→
690→        let rows: Vec<SummaryRow> = results
691→            .iter()
692→            .map(|r| {
693→                let detail = if r.op.success {
694→                    ok_count += 1;
695→                    "[ OK ]".to_string()
696→                } else {
697→                    failed_count += 1;
698→                    "[FAIL]".to_string()
699→                };
700→                SummaryRow {
701→                    name: r.op.name.clone(),
702→                    spec: r.op.spec.clone(),
703→                    detail,
704→                }
705→            })
706→            .collect();
707→
708→        print_summary_table(
709→            name,
710→            "COMPARE",
711→            "Status",
712→            &rows,
713→            &format!("Compare: {ok_count} ok, {failed_count} failed"),
714→        );
715→    }
716→
717→    if has_failure {
718→        ExitCode::from(1)
719→    } else {
720→        ExitCode::SUCCESS
721→    }
722→}
723→
724→/// Print a summary for run operations (shows exit codes, not pass/fail)
725→pub fn print_run_summary(name: &str, results: &[OpResult]) -> ExitCode {
726→    let mut error_count = 0;
727→
728→    let rows: Vec<SummaryRow> = results
729→        .iter()
730→        .map(|r| {
731→            let detail = if r.success {
732→                let code = r.message.strip_prefix("exit code: ").unwrap_or(&r.message);
733→                format!("{code:>4}")
734→            } else {
735→                error_count += 1;
736→                format!("ERROR: {}", r.message)
737→            };
738→            SummaryRow {
739→                name: r.name.clone(),
740→                spec: r.spec.clone(),
741→                detail,
742→            }
743→        })
744→        .collect();
745→
746→    let footer = if error_count > 0 {
747→        format!("Run: {error_count} error(s)")
748→    } else {
749→        String::new()
750→    };
751→
752→    print_summary_table(name, "RUN", "Exit", &rows, &footer);
753→
754→    if error_count > 0 {
755→        ExitCode::from(1)
756→    } else {
757→        ExitCode::SUCCESS
758→    }
759→}
