  1→use anyhow::{Context, Result, bail};
  2→use std::fs;
  3→use std::io::Write;
  4→use std::path::{Path, PathBuf};
  5→use std::process::Command;
  6→
  7→use crate::find_paths::{
  8→    find_package_dir, find_project_root, find_tspec, get_binary_path, get_binary_path_simple,
  9→    get_package_name,
 10→};
 11→use crate::tspec::{expand_target_dir, load_spec, spec_name_from_path};
 12→use crate::types::{Profile, Spec};
 13→
 14→const TSPEC_BUILD_RS_MARKER: &str = "// Generated by tspec - do not edit";
 15→pub fn warn_stale_build_rs(had_stale: bool) {
 16→    if had_stale {
 17→        println!("Warning: a stale build.rs was deleted, likely from a previous interrupted build");
 18→    }
 19→}
 20→
 21→/// Check if a build.rs file was generated by tspec.
 22→/// Matches either the marker comment or a file that only contains
 23→/// `cargo:rustc-link-arg-bin=` println lines (marker-less variant).
 24→fn is_tspec_generated_build_rs(content: &str) -> bool {
 25→    if content.starts_with(TSPEC_BUILD_RS_MARKER) {
 26→        return true;
 27→    }
 28→    // Fallback: file only contains fn main, cargo:rustc-link-arg-bin printlns, and braces
 29→    let dominated_by_link_args = content
 30→        .lines()
 31→        .map(|l| l.trim())
 32→        .filter(|l| !l.is_empty())
 33→        .all(|l| {
 34→            l == "fn main() {"
 35→                || l == "}"
 36→                || l.starts_with(TSPEC_BUILD_RS_MARKER)
 37→                || l.contains("cargo:rustc-link-arg-bin=")
 38→        });
 39→    dominated_by_link_args && content.contains("cargo:rustc-link-arg-bin=")
 40→}
 41→
 42→/// Remove a stale tspec-generated build.rs left behind by an interrupted build.
 43→/// Returns true if a stale file was removed. Caller should print a warning after the build.
 44→pub fn remove_stale_tspec_build_rs(pkg_dir: &Path) -> bool {
 45→    let build_rs = pkg_dir.join("build.rs");
 46→    if let Ok(content) = fs::read_to_string(&build_rs)
 47→        && is_tspec_generated_build_rs(&content)
 48→    {
 49→        let _ = fs::remove_file(&build_rs);
 50→        return true;
 51→    }
 52→    false
 53→}
 54→
 55→/// Result of a successful build
 56→pub struct BuildResult {
 57→    pub binary_path: PathBuf,
 58→    /// The effective target directory base (e.g., "target/foo" if target_dir was set)
 59→    pub target_base: PathBuf,
 60→}
 61→
 62→/// Build a package with a spec, returns the binary path on success
 63→pub fn build_package(pkg_name: &str, tspec: Option<&str>, release: bool) -> Result<BuildResult> {
 64→    let workspace = find_project_root()?;
 65→    let pkg_dir = find_package_dir(&workspace, pkg_name)?;
 66→    let tspec_path = find_tspec(&pkg_dir, tspec)?;
 67→
 68→    // Resolve actual package name from Cargo.toml (needed when pkg_name is a path)
 69→    let pkg_name = get_package_name(&pkg_dir)?;
 70→
 71→    // Clean up any stale tspec-generated build.rs from interrupted builds
 72→    let had_stale_build_rs = remove_stale_tspec_build_rs(&pkg_dir);
 73→
 74→    // Track if we generated a build.rs
 75→    let build_rs_path = pkg_dir.join("build.rs");
 76→    let had_build_rs = build_rs_path.exists();
 77→
 78→    // Load spec once (if present) and compute target_dir
 79→    let (spec, expanded_td) = if let Some(path) = &tspec_path {
 80→        let s = load_spec(path)?;
 81→        let name = spec_name_from_path(path);
 82→        let td = expand_target_dir(&s, &name)?;
 83→        (Some(s), td)
 84→    } else {
 85→        (None, None)
 86→    };
 87→
 88→    // Compute paths
 89→    let target_base = match &expanded_td {
 90→        Some(td) => workspace.join("target").join(td),
 91→        None => workspace.join("target"),
 92→    };
 93→    let binary_path = if let Some(spec) = &spec {
 94→        get_binary_path(&workspace, &pkg_name, spec, release, expanded_td.as_deref())
 95→    } else {
 96→        get_binary_path_simple(&workspace, &pkg_name, release)
 97→    };
 98→
 99→    // Apply spec if present, otherwise plain cargo build
100→    let status = if let Some(spec) = &spec {
101→        let path = tspec_path.as_ref().unwrap();
102→        println!("Building {} with spec {}", pkg_name, path.display());
103→
104→        // Generate temporary build.rs for linker flags if needed
105→        let has_linker_args = !spec.linker.args.is_empty();
106→        let has_bin_target = pkg_dir.join("src/main.rs").exists();
107→        if has_linker_args && !has_bin_target {
108→            println!(
109→                "Warning: linker.args ignored for {} (no binary target)",
110→                pkg_name
111→            );
112→        }
113→        if has_linker_args && has_bin_target && !had_build_rs {
114→            generate_build_rs(&build_rs_path, &pkg_name, spec)?;
115→        }
116→
117→        let mut cmd = build_cargo_command(spec)?;
118→        cmd.arg("build");
119→        cmd.arg("-p").arg(&pkg_name);
120→        cmd.current_dir(&workspace);
121→
122→        // Set spec path for tspec-build library to read in build.rs
123→        cmd.env("TSPEC_SPEC_FILE", path.as_os_str());
124→
125→        apply_spec_to_command(&mut cmd, spec, &workspace, release, expanded_td.as_deref())?;
126→        cmd.status().context("failed to run cargo")?
127→    } else {
128→        println!("Building {} (no tspec)", pkg_name);
129→        let mut cmd = Command::new("cargo");
130→        cmd.arg("build");
131→        cmd.arg("-p").arg(&pkg_name);
132→        cmd.current_dir(&workspace);
133→        if release {
134→            cmd.arg("--release");
135→        }
136→        cmd.status().context("failed to run cargo")?
137→    };
138→
139→    // Clean up generated build.rs (only if we created it)
140→    if !had_build_rs && build_rs_path.exists() {
141→        let _ = fs::remove_file(&build_rs_path);
142→    }
143→
144→    if !status.success() {
145→        bail!("cargo build failed");
146→    }
147→
148→    println!("  {}", binary_path.display());
149→    warn_stale_build_rs(had_stale_build_rs);
150→    Ok(BuildResult {
151→        binary_path,
152→        target_base,
153→    })
154→}
155→
156→/// Plain `cargo build --release` with no spec lookup.
157→/// Used by compare to produce a baseline build.
158→pub fn plain_cargo_build_release(pkg_name: &str) -> Result<BuildResult> {
159→    let workspace = find_project_root()?;
160→    let pkg_dir = find_package_dir(&workspace, pkg_name)?;
161→    let pkg_name = get_package_name(&pkg_dir)?;
162→
163→    // Clean up any stale tspec-generated build.rs from interrupted builds
164→    let had_stale_build_rs = remove_stale_tspec_build_rs(&pkg_dir);
165→
166→    let binary_path = get_binary_path_simple(&workspace, &pkg_name, true);
167→    let target_base = workspace.join("target");
168→
169→    println!("Building {} (no tspec)", pkg_name);
170→    let mut cmd = Command::new("cargo");
171→    cmd.arg("build");
172→    cmd.arg("-p").arg(&pkg_name);
173→    cmd.current_dir(&workspace);
174→    cmd.arg("--release");
175→    let status = cmd.status().context("failed to run cargo")?;
176→
177→    if !status.success() {
178→        bail!("cargo build failed");
179→    }
180→
181→    println!("  {}", binary_path.display());
182→    warn_stale_build_rs(had_stale_build_rs);
183→    Ok(BuildResult {
184→        binary_path,
185→        target_base,
186→    })
187→}
