349→mod tests {
350→    use super::*;
351→    use tempfile::TempDir;
352→
353→    #[test]
354→    fn remove_stale_tspec_build_rs_removes_marker_file() {
355→        let tmp = TempDir::new().unwrap();
356→        let build_rs = tmp.path().join("build.rs");
357→        fs::write(
358→            &build_rs,
359→            "// Generated by tspec - do not edit\nfn main() {\n}\n",
360→        )
361→        .unwrap();
362→
363→        assert!(remove_stale_tspec_build_rs(tmp.path()));
364→        assert!(!build_rs.exists());
365→    }
