367→    #[test]
368→    fn remove_stale_tspec_build_rs_preserves_user_file() {
369→        let tmp = TempDir::new().unwrap();
370→        let build_rs = tmp.path().join("build.rs");
371→        fs::write(
372→            &build_rs,
373→            "fn main() {\n    println!(\"cargo:rerun-if-changed=src\");\n}\n",
374→        )
375→        .unwrap();
376→
377→        assert!(!remove_stale_tspec_build_rs(tmp.path()));
378→        assert!(build_rs.exists());
