426→    #[test]
427→    fn target_json_adds_json_target_spec_flag() {
428→        let mut spec = Spec::default();
429→        spec.cargo.target_json = Some(PathBuf::from("x86_64-custom.json"));
430→
431→        let mut cmd = Command::new("cargo");
432→        cmd.arg("build");
433→        let workspace = PathBuf::from("/tmp/fake");
434→        apply_spec_to_command(&mut cmd, &spec, &workspace, false, None).unwrap();
435→
436→        let args: Vec<String> = cmd.get_args().map(|a| a.to_string_lossy().into()).collect();
437→        // Should have: -Z json-target-spec --target x86_64-custom.json
438→        let z_pos = args.iter().position(|a| a == "-Z").unwrap();
439→        assert_eq!(args[z_pos + 1], "json-target-spec");
440→        let target_pos = args.iter().position(|a| a == "--target").unwrap();
441→        assert_eq!(args[target_pos + 1], "x86_64-custom.json");
442→        assert!(
443→            z_pos < target_pos,
444→            "-Z json-target-spec should come before --target"
445→        );
446→    }
