464→    #[test]
465→    fn config_key_value_emits_config_args() {
466→        use crate::types::ConfigValue;
467→        use std::collections::BTreeMap;
468→
469→        let mut spec = Spec::default();
470→        spec.cargo.config_key_value = BTreeMap::from([
471→            (
472→                "profile.release.opt-level".to_string(),
473→                ConfigValue::String("s".to_string()),
474→            ),
475→            ("profile.release.lto".to_string(), ConfigValue::Bool(true)),
476→            (
477→                "profile.release.codegen-units".to_string(),
478→                ConfigValue::Integer(1),
479→            ),
480→        ]);
481→
482→        let mut cmd = Command::new("cargo");
483→        cmd.arg("build");
484→        let workspace = PathBuf::from("/tmp/fake");
485→        apply_spec_to_command(&mut cmd, &spec, &workspace, false, None).unwrap();
486→
487→        let args: Vec<String> = cmd.get_args().map(|a| a.to_string_lossy().into()).collect();
