  1→//! toml_edit helpers for surgical tspec editing that preserves comments/formatting.
  2→
  3→use anyhow::{Result, bail};
  4→use toml_edit::{Array, DocumentMut, Item, Value};
  5→
  6→/// Whether a field holds a scalar, an array, or a table.
  7→#[derive(Debug, Clone, Copy, PartialEq, Eq)]
  8→pub enum FieldKind {
  9→    Scalar,
 10→    Array,
 11→    Table,
 12→}
 13→
 14→/// Registry entry: (dotted key path, kind).
 15→const FIELD_REGISTRY: &[(&str, FieldKind)] = &[
 16→    ("panic", FieldKind::Scalar),
 17→    ("strip", FieldKind::Scalar),
 18→    ("cargo.profile", FieldKind::Scalar),
 19→    ("cargo.target_triple", FieldKind::Scalar),
 20→    ("cargo.target_json", FieldKind::Scalar),
 21→    ("cargo.target_dir", FieldKind::Scalar),
 22→    ("cargo.unstable", FieldKind::Array),
 23→    ("cargo.config_key_value", FieldKind::Table),
 24→    ("rustc.build_std", FieldKind::Array),
 25→    ("rustc.flags", FieldKind::Array),
 26→    ("linker.args", FieldKind::Array),
 27→];
 28→
 29→/// Validate that a key is in the registry and return its kind.
 30→/// Also accepts table sub-keys like `cargo.config_key_value."profile.release.opt-level"`.
 31→pub fn validate_key(key: &str) -> Result<FieldKind> {
 32→    for &(k, kind) in FIELD_REGISTRY {
 33→        if k == key {
 34→            return Ok(kind);
 35→        }
 36→    }
 37→    // Check if it's a table sub-key
 38→    if parse_table_key(key).is_some() {
 39→        return Ok(FieldKind::Table);
 40→    }
 41→    let valid_keys: Vec<&str> = FIELD_REGISTRY.iter().map(|(k, _)| *k).collect();
 42→    bail!(
 43→        "unknown key: {} (valid keys: {})",
 44→        key,
 45→        valid_keys.join(", ")
 46→    )
 47→}
 48→
 49→/// Parse a key that may reference a sub-key within a Table field.
 50→/// E.g., `cargo.config_key_value."profile.release.opt-level"` → Some(("cargo.config_key_value", "profile.release.opt-level"))
 51→/// Also accepts unquoted sub-keys: `cargo.config_key_value.profile.release.opt-level` → same result.
 52→/// Returns None if the key doesn't start with a known Table field prefix.
 53→pub fn parse_table_key(key: &str) -> Option<(&str, &str)> {
 54→    for &(prefix, kind) in FIELD_REGISTRY {
 55→        if kind != FieldKind::Table {
 56→            continue;
 57→        }
 58→        if let Some(rest) = key.strip_prefix(prefix)
 59→            && let Some(sub_key) = rest.strip_prefix('.')
 60→        {
 61→            if sub_key.is_empty() {
 62→                return None;
 63→            }
 64→            // Strip surrounding quotes if present
 65→            let sub_key = sub_key
 66→                .strip_prefix('"')
 67→                .and_then(|s| s.strip_suffix('"'))
 68→                .unwrap_or(sub_key);
 69→            if sub_key.is_empty() {
 70→                return None;
 71→            }
 72→            return Some((prefix, sub_key));
 73→        }
 74→    }
 75→    None
 76→}
 77→
 78→/// Validate a value for enum-constrained fields.
 79→/// For unconstrained fields (strings, arrays), accepts anything.
 80→pub fn validate_value(key: &str, value: &str) -> Result<()> {
 81→    match key {
 82→        "panic" => match value {
 83→            "unwind" | "abort" | "immediate-abort" => Ok(()),
 84→            _ => bail!(
 85→                "invalid panic mode: {} (expected: unwind, abort, immediate-abort)",
 86→                value
 87→            ),
 88→        },
 89→        "strip" => match value {
 90→            "none" | "debuginfo" | "symbols" => Ok(()),
 91→            _ => bail!(
 92→                "invalid strip mode: {} (expected: none, debuginfo, symbols)",
 93→                value
 94→            ),
 95→        },
 96→        "cargo.profile" => match value {
 97→            "debug" | "release" => Ok(()),
 98→            _ => bail!("invalid profile: {} (expected: debug, release)", value),
 99→        },
100→        _ => Ok(()),
101→    }
102→}
103→
104→/// Parse a key into (optional table, field).
105→/// "panic" -> (None, "panic")
106→/// "rustc.lto" -> (Some("rustc"), "lto")
107→fn parse_key(key: &str) -> (Option<&str>, &str) {
108→    match key.split_once('.') {
109→        Some((table, field)) => (Some(table), field),
110→        None => (None, key),
111→    }
112→}
113→
114→/// Parse a value string into a toml_edit Value.
115→/// All scalar fields are stored as strings.
116→fn parse_scalar_value(_key: &str, raw: &str) -> Value {
117→    Value::from(raw)
118→}
119→
120→/// Get the existing array for a field, or an empty array if it doesn't exist.
121→fn get_existing_array(doc: &DocumentMut, key: &str) -> Array {
122→    let (table_name, field) = parse_key(key);
123→    match table_name {
124→        Some(table) => doc
125→            .get(table)
126→            .and_then(|t| t.get(field))
127→            .and_then(|v| v.as_array())
128→            .cloned()
129→            .unwrap_or_default(),
130→        None => doc
131→            .get(field)
132→            .and_then(|v| v.as_array())
133→            .cloned()
134→            .unwrap_or_default(),
135→    }
136→}
137→
138→/// Set an array value into the document at the given key path.
139→fn set_array_in_doc(doc: &mut DocumentMut, key: &str, arr: Array) {
140→    let (table_name, field) = parse_key(key);
141→    match table_name {
142→        Some(table) => {
143→            ensure_table(doc, table);
144→            doc[table][field] = Item::Value(Value::Array(arr));
145→        }
146→        None => {
147→            doc[field] = Item::Value(Value::Array(arr));
148→        }
149→    }
150→}
151→
152→/// Ensure a table exists in the document.
153→fn ensure_table(doc: &mut DocumentMut, table: &str) {
154→    if doc.get(table).is_none() {
155→        doc[table] = Item::Table(toml_edit::Table::new());
156→    }
157→}
158→
159→// === Public API: takes &[String] from shell args, no string parsing ===
160→
161→/// Set a field from string args. Scalars take `values[0]`, arrays take all values.
162→pub fn set_field(
163→    doc: &mut DocumentMut,
164→    key: &str,
165→    values: &[String],
166→    kind: FieldKind,
167→) -> Result<()> {
168→    let (table_name, field) = parse_key(key);
169→
170→    match kind {
171→        FieldKind::Scalar => {
172→            if values.len() != 1 {
173→                bail!(
174→                    "scalar field '{}' requires exactly one value, got {}",
175→                    key,
176→                    values.len()
177→                );
178→            }
179→            let val = parse_scalar_value(key, &values[0]);
180→            match table_name {
181→                Some(table) => {
182→                    ensure_table(doc, table);
183→                    doc[table][field] = Item::Value(val);
184→                }
185→                None => {
186→                    doc[field] = Item::Value(val);
187→                }
188→            }
189→        }
190→        FieldKind::Array => {
191→            let mut arr = Array::new();
192→            for v in values {
193→                arr.push(v.as_str());
194→            }
195→            set_array_in_doc(doc, key, arr);
196→        }
197→        FieldKind::Table => {
198→            bail!(
199→                "use set_table_value() for table field '{}'; set_field() does not handle tables",
200→                key
201→            );
202→        }
203→    }
204→
205→    Ok(())
206→}
207→
208→/// Add items to an array field. Appends by default, or inserts at `index`.
209→/// Deduplicates on append; insert adds at position without dedup.
210→pub fn add_items(
211→    doc: &mut DocumentMut,
212→    key: &str,
213→    values: &[String],
214→    index: Option<usize>,
215→) -> Result<()> {
216→    let mut arr = get_existing_array(doc, key);
217→
218→    match index {
219→        Some(idx) => {
220→            if idx > arr.len() {
221→                bail!(
222→                    "index {} out of bounds for array '{}' with {} elements",
223→                    idx,
224→                    key,
225→                    arr.len()
226→                );
227→            }
228→            // Insert at position (no dedup — user explicitly chose position)
229→            for (offset, v) in values.iter().enumerate() {
230→                arr.insert(idx + offset, v.as_str());
231→            }
232→        }
233→        None => {
234→            // Append with dedup
235→            let existing: Vec<String> = arr
236→                .iter()
237→                .filter_map(|v| v.as_str().map(|s| s.to_string()))
238→                .collect();
239→            for v in values {
240→                if !existing.contains(v) {
241→                    arr.push(v.as_str());
242→                }
243→            }
244→        }
245→    }
246→
247→    set_array_in_doc(doc, key, arr);
248→    Ok(())
249→}
250→
251→/// Remove items from an array field by value.
252→/// Keeps the field as an empty array if all items are removed.
253→pub fn remove_items_by_value(doc: &mut DocumentMut, key: &str, values: &[String]) -> Result<()> {
254→    let arr = get_existing_array(doc, key);
255→
256→    let mut new_arr = Array::new();
257→    for item in arr.iter() {
258→        if let Some(s) = item.as_str()
259→            && !values.iter().any(|v| v == s)
260→        {
261→            new_arr.push(s);
262→        }
263→    }
264→
265→    set_array_in_doc(doc, key, new_arr);
266→    Ok(())
267→}
268→
269→/// Remove an item from an array field by index.
270→/// Keeps the field as an empty array if the last item is removed.
271→pub fn remove_item_by_index(doc: &mut DocumentMut, key: &str, index: usize) -> Result<()> {
272→    let mut arr = get_existing_array(doc, key);
273→
274→    if index >= arr.len() {
275→        bail!(
276→            "index {} out of bounds for array '{}' with {} elements",
277→            index,
278→            key,
279→            arr.len()
280→        );
281→    }
282→
283→    arr.remove(index);
284→    set_array_in_doc(doc, key, arr);
285→    Ok(())
286→}
287→
288→/// Remove a field from a toml_edit document.
289→/// Does not remove the containing table, even if it becomes empty.
290→pub fn unset_field(doc: &mut DocumentMut, key: &str) -> Result<()> {
291→    let (table_name, field) = parse_key(key);
292→
293→    match table_name {
294→        Some(table) => {
295→            if let Some(Item::Table(tbl)) = doc.get_mut(table) {
296→                tbl.remove(field);
297→            }
298→        }
299→        None => {
300→            doc.remove(field);
301→        }
302→    }
303→
304→    Ok(())
305→}
306→
307→/// Smart-parse a raw value string into a toml_edit Value for table entries.
308→/// Booleans → bool, integers → i64, everything else → string.
309→fn parse_smart_value(raw: &str) -> Value {
310→    match raw {
311→        "true" => Value::from(true),
312→        "false" => Value::from(false),
313→        _ => {
314→            if let Ok(n) = raw.parse::<i64>() {
315→                Value::from(n)
316→            } else {
317→                Value::from(raw)
318→            }
319→        }
320→    }
321→}
322→
323→/// Set a value in a table field (e.g., `cargo.config_key_value`).
324→/// `table_path` is the dotted path to the table (e.g., "cargo.config_key_value").
325→/// `sub_key` is the key within that table (e.g., "profile.release.opt-level").
326→/// `raw_value` is the string value to set (auto-parsed to bool/int/string).
327→pub fn set_table_value(
328→    doc: &mut DocumentMut,
329→    table_path: &str,
330→    sub_key: &str,
331→    raw_value: &str,
332→) -> Result<()> {
333→    let (parent, table_name) = parse_key(table_path);
334→    let val = parse_smart_value(raw_value);
335→
336→    match parent {
337→        Some(p) => {
338→            ensure_table(doc, p);
339→            // Ensure the nested table exists
340→            if doc[p].get(table_name).is_none() {
341→                doc[p][table_name] = Item::Table(toml_edit::Table::new());
342→            }
343→            doc[p][table_name][sub_key] = Item::Value(val);
344→        }
345→        None => {
346→            if doc.get(table_name).is_none() {
347→                doc[table_name] = Item::Table(toml_edit::Table::new());
348→            }
349→            doc[table_name][sub_key] = Item::Value(val);
350→        }
351→    }
352→
353→    Ok(())
354→}
355→
356→/// Remove a single key from a table field.
357→pub fn unset_table_value(doc: &mut DocumentMut, table_path: &str, sub_key: &str) -> Result<()> {
358→    let (parent, table_name) = parse_key(table_path);
359→
360→    match parent {
361→        Some(p) => {
362→            if let Some(Item::Table(parent_tbl)) = doc.get_mut(p)
363→                && let Some(Item::Table(tbl)) = parent_tbl.get_mut(table_name)
364→            {
365→                tbl.remove(sub_key);
366→            }
367→        }
368→        None => {
369→            if let Some(Item::Table(tbl)) = doc.get_mut(table_name) {
370→                tbl.remove(sub_key);
371→            }
372→        }
373→    }
374→
375→    Ok(())
376→}
