  1→use anyhow::{Context, Result};
  2→use sha2::{Digest, Sha256};
  3→use std::path::{Path, PathBuf};
  4→
  5→use crate::TSPEC_SUFFIX;
  6→use crate::types::Spec;
  7→
  8→/// Load a spec from a TOML file
  9→pub fn load_spec(path: &Path) -> Result<Spec> {
 10→    let content = std::fs::read_to_string(path)
 11→        .with_context(|| format!("failed to read spec file: {}", path.display()))?;
 12→    parse_spec(&content)
 13→}
 14→
 15→/// Parse a spec from TOML string
 16→pub fn parse_spec(toml_str: &str) -> Result<Spec> {
 17→    toml::from_str(toml_str).context("failed to parse spec TOML")
 18→}
 19→
 20→/// Serialize a spec to TOML string (canonical form for hashing)
 21→pub fn serialize_spec(spec: &Spec) -> Result<String> {
 22→    toml::to_string(spec).context("failed to serialize spec")
 23→}
 24→
 25→/// Compute hash of a resolved spec (first 8 hex chars)
 26→pub fn hash_spec(spec: &Spec) -> Result<String> {
 27→    let toml_str = serialize_spec(spec)?;
 28→    let mut hasher = Sha256::new();
 29→    hasher.update(toml_str.as_bytes());
 30→    let result = hasher.finalize();
 31→    Ok(hex::encode(&result[..4]))
 32→}
 33→
 34→/// Extract spec name from path by stripping the .ts.toml (or .toml) suffix
 35→pub fn spec_name_from_path(path: &Path) -> String {
 36→    let filename = path
 37→        .file_name()
 38→        .map(|s| s.to_string_lossy().to_string())
 39→        .unwrap_or_default();
 40→
 41→    filename
 42→        .strip_suffix(TSPEC_SUFFIX)
 43→        .or_else(|| filename.strip_suffix(".toml"))
 44→        .unwrap_or(&filename)
 45→        .to_string()
 46→}
 47→
 48→/// Expand template placeholders in a spec's target_dir field.
 49→/// Returns None if target_dir is absent or empty.
 50→pub fn expand_target_dir(spec: &Spec, spec_name: &str) -> Result<Option<String>> {
 51→    let raw = match &spec.cargo.target_dir {
 52→        Some(td) if !td.is_empty() => td,
 53→        _ => return Ok(None),
 54→    };
 55→
 56→    let mut expanded = raw.clone();
 57→
 58→    if expanded.contains("{name}") {
 59→        expanded = expanded.replace("{name}", spec_name);
 60→    }
 61→
 62→    if expanded.contains("{hash}") {
 63→        let hash = hash_spec(spec)?;
 64→        expanded = expanded.replace("{hash}", &hash);
 65→    }
 66→
 67→    if expanded.is_empty() {
 68→        Ok(None)
 69→    } else {
 70→        Ok(Some(expanded))
 71→    }
 72→}
 73→
 74→/// Save a spec to a TOML file, creating parent directories if needed
 75→pub fn save_spec(spec: &Spec, path: &Path) -> Result<()> {
 76→    if let Some(parent) = path.parent() {
 77→        std::fs::create_dir_all(parent)
 78→            .with_context(|| format!("failed to create directory: {}", parent.display()))?;
 79→    }
 80→    let content = serialize_spec(spec)?;
 81→    std::fs::write(path, &content)
 82→        .with_context(|| format!("failed to write spec file: {}", path.display()))
 83→}
 84→
 85→/// Find the next sequence number for snapshot files matching `{name}-NNN-*{TSPEC_SUFFIX}`.
 86→pub fn next_snapshot_seq(name: &str, dir: &Path) -> Result<u32> {
 87→    let prefix = format!("{}-", name);
 88→    let next_seq = std::fs::read_dir(dir)
 89→        .with_context(|| format!("failed to read directory: {}", dir.display()))?
 90→        .filter_map(|entry| entry.ok())
 91→        .filter_map(|entry| {
 92→            let filename = entry.file_name().to_string_lossy().into_owned();
 93→            if filename.starts_with(&prefix) && filename.ends_with(TSPEC_SUFFIX) {
 94→                let rest = filename.strip_prefix(&prefix)?;
 95→                let seq_str = rest.split('-').next()?;
 96→                seq_str.parse::<u32>().ok()
 97→            } else {
 98→                None
 99→            }
100→        })
101→        .max()
102→        .map(|n| n + 1)
103→        .unwrap_or(1);
104→    Ok(next_seq)
105→}
106→
107→/// Save a spec snapshot with sequence number and hash: `{name}-{seq:03}-{hash}.toml`
108→/// Returns the path of the created file.
109→pub fn save_spec_snapshot(spec: &Spec, name: &str, dir: &Path) -> Result<PathBuf> {
110→    std::fs::create_dir_all(dir)
111→        .with_context(|| format!("failed to create directory: {}", dir.display()))?;
112→
113→    let next_seq = next_snapshot_seq(name, dir)?;
114→    let hash = hash_spec(spec)?;
115→    let filename = format!("{}-{:03}-{}{}", name, next_seq, hash, TSPEC_SUFFIX);
116→    let path = dir.join(&filename);
117→
118→    let content = serialize_spec(spec)?;
119→    std::fs::write(&path, &content)
120→        .with_context(|| format!("failed to write spec file: {}", path.display()))?;
121→
122→    Ok(path)
123→}
124→
125→/// Create a backup snapshot using raw file copy (preserves comments/formatting).
126→/// Loads the spec only for hashing (to compute the backup filename).
127→/// Returns the path of the created backup file.
128→pub fn copy_spec_snapshot(source: &Path, name: &str, dir: &Path) -> Result<PathBuf> {
129→    std::fs::create_dir_all(dir)
130→        .with_context(|| format!("failed to create directory: {}", dir.display()))?;
131→
132→    let spec = load_spec(source)?;
133→    let next_seq = next_snapshot_seq(name, dir)?;
134→    let hash = hash_spec(&spec)?;
135→    let filename = format!("{}-{:03}-{}{}", name, next_seq, hash, TSPEC_SUFFIX);
136→    let path = dir.join(&filename);
137→
138→    std::fs::copy(source, &path)
139→        .with_context(|| format!("failed to copy {} to {}", source.display(), path.display()))?;
140→
141→    Ok(path)
142→}
