use serde::{Deserialize, Serialize};
use std::collections::BTreeMap;
use std::fmt;
use std::path::PathBuf;

use crate::options::{PanicMode, StripMode};

/// Build profile - mutually exclusive
#[derive(Debug, Clone, PartialEq, Eq, Serialize, Deserialize)]
#[serde(rename_all = "lowercase")]
pub enum Profile {
    Debug,
    Release,
}

/// A value in the `[cargo.config_key_value]` table.
/// Uses `#[serde(untagged)]` so TOML bools/ints/strings are deserialized naturally.
/// We avoid `toml::Value` because it contains `Float(f64)` which doesn't implement `Eq`.
#[derive(Debug, Clone, PartialEq, Eq, Serialize, Deserialize)]
#[serde(untagged)]
pub enum ConfigValue {
    Bool(bool),
    Integer(i64),
    String(String),
}

impl fmt::Display for ConfigValue {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        match self {
            ConfigValue::Bool(b) => write!(f, "{}", b),
            ConfigValue::Integer(n) => write!(f, "{}", n),
            ConfigValue::String(s) => write!(f, "\"{}\"", s),
        }
    }
}

/// Cargo-specific configuration (flat struct)
#[derive(Debug, Clone, Default, PartialEq, Eq, Serialize, Deserialize)]
pub struct CargoConfig {
    /// Build profile (debug or release)
    pub profile: Option<Profile>,
    /// Target triple (e.g., "x86_64-unknown-linux-musl")
    pub target_triple: Option<String>,
    /// Custom target JSON file path
    pub target_json: Option<PathBuf>,
    /// Nightly-only -Z flags (e.g., ["panic-immediate-abort"])
    #[serde(default)]
    pub unstable: Vec<String>,
    /// Custom target directory subdirectory for per-spec isolation.
    /// Supports `{name}` (spec filename sans .ts.toml) and `{hash}` (8-char content hash).
    pub target_dir: Option<String>,
    /// Key-value pairs passed as `--config 'KEY=VALUE'` to cargo.
    /// Each entry becomes a separate `--config` arg.
    #[serde(default)]
    pub config_key_value: BTreeMap<String, ConfigValue>,
    /// Crates to rebuild with -Z build-std (nightly only)
    #[serde(default)]
    pub build_std: Vec<String>,
}

/// Version script configuration for symbol visibility
#[derive(Debug, Clone, PartialEq, Eq, Serialize, Deserialize)]
pub struct VersionScript {
    /// Symbols to keep global (e.g., ["_start"])
    pub global: Vec<String>,
    /// Pattern for local symbols (typically "*")
    #[serde(default = "default_local")]
    pub local: String,
}

fn default_local() -> String {
    "*".to_string()
}

/// Linker configuration (flat struct)
#[derive(Debug, Clone, Default, PartialEq, Eq, Serialize, Deserialize)]
pub struct LinkerConfig {
    /// Linker arguments (e.g., ["-static", "-nostdlib"])
    #[serde(default)]
    pub args: Vec<String>,
    /// Version script for symbol visibility (enables --gc-sections optimization)
    pub version_script: Option<VersionScript>,
}

/// A translation spec
#[derive(Debug, Clone, Default, PartialEq, Eq, Serialize, Deserialize)]
pub struct Spec {
    /// High-level panic mode (sets both cargo -Z and rustc -C flags)
    pub panic: Option<PanicMode>,

    /// High-level strip mode (sets rustc -C strip=)
    pub strip: Option<StripMode>,

    #[serde(default)]
    pub cargo: CargoConfig,
    /// Raw flags passed through to RUSTFLAGS
    #[serde(default)]
    pub rustflags: Vec<String>,
    #[serde(default)]
    pub linker: LinkerConfig,
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn spec_default_is_empty() {
        let spec = Spec::default();
        assert_eq!(spec.cargo, CargoConfig::default());
        assert!(spec.rustflags.is_empty());
        assert_eq!(spec.linker, LinkerConfig::default());
    }
}

//! toml_edit helpers for surgical tspec editing that preserves comments/formatting.

use anyhow::{Result, bail};
use toml_edit::{Array, DocumentMut, Item, Value};

/// Whether a field holds a scalar, an array, or a table.
#[derive(Debug, Clone, Copy, PartialEq, Eq)]
pub enum FieldKind {
    Scalar,
    Array,
    Table,
}

/// Registry entry: (dotted key path, kind).
const FIELD_REGISTRY: &[(&str, FieldKind)] = &[
    ("panic", FieldKind::Scalar),
    ("strip", FieldKind::Scalar),
    ("cargo.profile", FieldKind::Scalar),
    ("cargo.target_triple", FieldKind::Scalar),
    ("cargo.target_json", FieldKind::Scalar),
    ("cargo.target_dir", FieldKind::Scalar),
    ("cargo.unstable", FieldKind::Array),
    ("cargo.config_key_value", FieldKind::Table),
    ("cargo.build_std", FieldKind::Array),
    ("rustflags", FieldKind::Array),
    ("linker.args", FieldKind::Array),
];

/// Validate that a key is in the registry and return its kind.
/// Also accepts table sub-keys like `cargo.config_key_value."profile.release.opt-level"`.
