173→/// Run all app packages sequentially
174→///
175→/// When `tspec_patterns` is empty, each package uses its default spec.
176→/// When non-empty, patterns are resolved per-package; packages with no matches are skipped.
177→pub fn run_all(
178→    workspace: &WorkspaceInfo,
179→    tspec_patterns: &[String],
180→    cli_profile: Option<&str>,
181→    strip: bool,
182→    verbosity: Verbosity,
183→) -> Vec<OpResult> {
184→    let normalized = match normalize_tspec_patterns(tspec_patterns) {
185→        Some(n) => n,
186→        None => {
187→            warn_shell_glob_expansion(tspec_patterns);
188→            return Vec::new();
189→        }
190→    };
191→    let mut results = Vec::new();
192→
193→    for member in workspace.runnable_members() {
194→        let specs = resolve_specs_for_member(&member.path, &normalized);
195→        if specs.is_empty() && !normalized.is_empty() {
196→            continue;
197→        }
198→
199→        println!("=== {} ===", member.name);
200→
201→        let tspec_list: Vec<Option<String>> = if specs.is_empty() {
202→            vec![None]
203→        } else {
204→            specs
205→                .into_iter()
206→                .map(|p| Some(p.to_string_lossy().into_owned()))
207→                .collect()
208→        };
209→
210→        for tspec in &tspec_list {
211→            let spec = spec_label(tspec);
212→            let result = match build_package(&member.name, tspec.as_deref(), cli_profile, verbosity)
213→            {
214→                Ok(build_result) => {
215→                    if strip && let Err(e) = strip_binary(&build_result.binary_path) {
216→                        eprintln!("  warning: strip failed: {}", e);
217→                    }
218→                    match run_binary(&build_result.binary_path, &[]) {
219→                        Ok(exit_code) => OpResult {
220→                            name: member.name.clone(),
221→                            spec: spec.clone(),
222→                            success: true,
223→                            message: format!("exit code: {}", exit_code),
224→                            size: None,
225→                        },
226→                        Err(e) => OpResult {
227→                            name: member.name.clone(),
228→                            spec: spec.clone(),
229→                            success: false,
230→                            message: format!("run failed: {}", e),
231→                            size: None,
232→                        },
233→                    }
234→                }
235→                Err(e) => OpResult {
236→                    name: member.name.clone(),
237→                    spec,
238→                    success: false,
239→                    message: format!("build failed: {}", e),
240→                    size: None,
241→                },
242→            };
243→
244→            results.push(result);
245→        }
246→    }
247→
248→    results
249→}
