251→/// Test all workspace packages
252→///
253→/// When `tspec_patterns` is empty, each package uses its default spec.
254→/// When non-empty, patterns are resolved per-package; packages with no matches are skipped.
255→pub fn test_all(
256→    workspace: &WorkspaceInfo,
257→    tspec_patterns: &[String],
258→    cli_profile: Option<&str>,
259→    fail_fast: bool,
260→    verbosity: Verbosity,
261→) -> Vec<OpResult> {
262→    let normalized = match normalize_tspec_patterns(tspec_patterns) {
263→        Some(n) => n,
264→        None => {
265→            warn_shell_glob_expansion(tspec_patterns);
266→            return Vec::new();
267→        }
268→    };
269→    let mut results = Vec::new();
270→
271→    // Test regular packages (excluding Test kind which needs special handling)
272→    for member in workspace.buildable_members() {
273→        if member.kind == PackageKind::Test {
274→            continue; // Handle test packages separately
275→        }
276→
277→        let specs = resolve_specs_for_member(&member.path, &normalized);
278→        if specs.is_empty() && !normalized.is_empty() {
279→            continue;
280→        }
281→
282→        println!("=== {} ===", member.name);
283→
284→        let tspec_list: Vec<Option<String>> = if specs.is_empty() {
285→            vec![None]
286→        } else {
287→            specs
288→                .into_iter()
289→                .map(|p| Some(p.to_string_lossy().into_owned()))
290→                .collect()
291→        };
292→
293→        for tspec in &tspec_list {
294→            let spec = spec_label(tspec);
295→            let result = match test_package(&member.name, tspec.as_deref(), cli_profile, verbosity)
296→            {
297→                Ok(()) => OpResult {
298→                    name: member.name.clone(),
299→                    spec,
300→                    success: true,
301→                    message: "ok".to_string(),
302→                    size: None,
303→                },
304→                Err(e) => OpResult {
305→                    name: member.name.clone(),
306→                    spec,
307→                    success: false,
308→                    message: e.to_string(),
309→                    size: None,
310→                },
311→            };
312→
313→            let failed = !result.success;
314→            results.push(result);
315→
316→            if failed && fail_fast {
317→                return results;
318→            }
319→        }
320→    }
321→
322→    // Handle test packages (like rlibc-x2-tests) - build and run all test binaries
323→    for member in workspace.test_members() {
324→        let specs = resolve_specs_for_member(&member.path, &normalized);
325→        if specs.is_empty() && !normalized.is_empty() {
326→            continue;
327→        }
328→
329→        println!("=== {} ===", member.name);
330→
331→        // Build the test package (builds all binaries)
332→        let build_tspec = specs.first().map(|p| p.to_string_lossy().into_owned());
333→        let spec = spec_label(&build_tspec);
334→        let build_result =
335→            match build_package(&member.name, build_tspec.as_deref(), cli_profile, verbosity) {
336→                Ok(r) => r,
337→                Err(e) => {
338→                    results.push(OpResult {
339→                        name: member.name.clone(),
340→                        spec,
341→                        success: false,
342→                        message: format!("build failed: {}", e),
343→                        size: None,
344→                    });
345→                    if fail_fast {
346→                        return results;
347→                    }
348→                    continue;
349→                }
350→            };
351→
352→        // Find and run all test binaries in the target directory
353→        let profile_dir = crate::types::profile_dir_name(cli_profile.unwrap_or("debug"));
354→        let target_dir = build_result.target_base.join(profile_dir);
355→
356→        // Look for binaries that end with "-tests" in the target directory
357→        let test_binaries: Vec<_> = std::fs::read_dir(&target_dir)
358→            .into_iter()
359→            .flatten()
360→            .filter_map(|e| e.ok())
361→            .filter(|e| {
362→                let name = e.file_name().to_string_lossy().to_string();
363→                let is_file = e.path().is_file();
364→                let is_test = name.ends_with("-tests");
365→                let is_executable = e
366→                    .metadata()
367→                    .map(|m| m.permissions().mode() & 0o111 != 0)
368→                    .unwrap_or(false);
369→                is_file && is_test && is_executable
370→            })
371→            .collect();
372→
373→        if test_binaries.is_empty() {
374→            results.push(OpResult {
375→                name: member.name.clone(),
376→                spec: spec.clone(),
377→                success: false,
378→                message: "no test binaries found".to_string(),
379→                size: None,
380→            });
381→            continue;
382→        }
383→
384→        for entry in test_binaries {
385→            let path = entry.path();
386→            let bin_name = entry.file_name().to_string_lossy().to_string();
387→
388→            print!("  Running {}... ", bin_name);
389→
390→            let result = match run_binary(&path, &[]) {
391→                Ok(exit_code) => {
392→                    if exit_code == 0 {
393→                        println!("ok");
394→                        OpResult {
395→                            name: format!("{}/{}", member.name, bin_name),
396→                            spec: spec.clone(),
397→                            success: true,
398→                            message: "ok".to_string(),
399→                            size: None,
400→                        }
401→                    } else {
402→                        println!("FAILED (exit {})", exit_code);
403→                        OpResult {
404→                            name: format!("{}/{}", member.name, bin_name),
405→                            spec: spec.clone(),
406→                            success: false,
407→                            message: format!("exit code: {}", exit_code),
408→                            size: None,
409→                        }
410→                    }
411→                }
412→                Err(e) => {
413→                    println!("FAILED");
414→                    OpResult {
415→                        name: format!("{}/{}", member.name, bin_name),
416→                        spec: spec.clone(),
417→                        success: false,
418→                        message: format!("run failed: {}", e),
419→                        size: None,
420→                    }
421→                }
422→            };
423→
424→            let failed = !result.success;
425→            results.push(result);
426→
427→            if failed && fail_fast {
428→                return results;
429→            }
430→        }
431→    }
432→
433→    results
434→}
