436→/// A row for the summary table: package name, spec, and pre-formatted detail string.
437→struct SummaryRow {
438→    name: String,
439→    spec: String,
440→    detail: String,
441→}
442→
443→/// Print a summary table with consistent formatting across all operations.
444→///
445→/// - `ws_name` — workspace/package name for the header
446→/// - `cmd` — operation name ("TEST", "BUILD", "RUN", "COMPARE")
447→/// - `detail_header` — column header for the detail column (e.g. "Status", "Status    Size", "Exit")
448→/// - `rows` — pre-formatted rows
449→/// - `footer` — footer line (e.g. "Test: 3 passed, 1 failed")
450→fn print_summary_table(
451→    ws_name: &str,
452→    cmd: &str,
453→    detail_header: &str,
454→    rows: &[SummaryRow],
455→    footer: &str,
456→) {
457→    let max_name_len = rows.iter().map(|r| r.name.len()).max().unwrap_or(7).max(7);
458→    let has_spec = rows.iter().any(|r| !r.spec.is_empty());
459→    let max_spec_len = if has_spec {
460→        rows.iter().map(|r| r.spec.len()).max().unwrap_or(4).max(4)
461→    } else {
462→        0
463→    };
464→
465→    println!();
466→    print_header!(format!("{ws_name} {cmd} SUMMARY"));
467→    if has_spec {
468→        println!(
469→            "  {:nw$}  {:sw$}  {detail_header}",
470→            "Package",
471→            "Spec",
472→            nw = max_name_len,
473→            sw = max_spec_len
474→        );
475→    } else {
476→        println!(
477→            "  {:width$}  {detail_header}",
478→            "Package",
479→            width = max_name_len
480→        );
481→    }
482→
483→    for row in rows {
484→        if has_spec {
485→            println!(
486→                "  {:nw$}  {:sw$}  {}",
487→                row.name,
488→                row.spec,
489→                row.detail,
490→                nw = max_name_len,
491→                sw = max_spec_len
492→            );
493→        } else {
494→            println!(
495→                "  {:width$}  {}",
496→                row.name,
497→                row.detail,
498→                width = max_name_len
499→            );
500→        }
501→    }
502→
503→    println!();
504→    if !footer.is_empty() {
505→        println!("  {footer}");
506→    }
507→    print_hline!();
508→    println!();
509→}
