511→/// Print a summary of operation results (for tests)
512→pub fn print_test_summary(name: &str, results: &[OpResult]) -> ExitCode {
513→    let mut passed = 0;
514→    let mut failed = 0;
515→
516→    let rows: Vec<SummaryRow> = results
517→        .iter()
518→        .map(|r| {
519→            let detail = if r.success {
520→                passed += 1;
521→                "[PASS]".to_string()
522→            } else {
523→                failed += 1;
524→                "[FAIL]".to_string()
525→            };
526→            SummaryRow {
527→                name: r.name.clone(),
528→                spec: r.spec.clone(),
529→                detail,
530→            }
531→        })
532→        .collect();
533→
534→    print_summary_table(
535→        name,
536→        "TEST",
537→        "Status",
538→        &rows,
539→        &format!("Test: {passed} passed, {failed} failed"),
540→    );
541→
542→    if failed > 0 {
543→        ExitCode::from(1)
544→    } else {
545→        ExitCode::SUCCESS
546→    }
547→}
548→
549→/// Print a summary for build operations (OK/FAILED)
550→pub fn print_summary(name: &str, results: &[OpResult]) -> ExitCode {
551→    let mut ok_count = 0;
552→    let mut failed_count = 0;
553→
554→    let rows: Vec<SummaryRow> = results
555→        .iter()
556→        .map(|r| {
557→            let status = if r.success {
558→                ok_count += 1;
559→                "[ OK ]"
560→            } else {
561→                failed_count += 1;
562→                "[FAIL]"
563→            };
564→            let size_str = r.size.map(format_size).unwrap_or_else(|| "--".to_string());
565→            SummaryRow {
566→                name: r.name.clone(),
567→                spec: r.spec.clone(),
568→                detail: format!("{status}  {size_str:>6}"),
569→            }
570→        })
571→        .collect();
572→
573→    print_summary_table(
574→        name,
575→        "BUILD",
576→        "Status    Size",
577→        &rows,
578→        &format!("Build: {ok_count} ok, {failed_count} failed"),
579→    );
580→
581→    if failed_count > 0 {
582→        ExitCode::from(1)
583→    } else {
584→        ExitCode::SUCCESS
585→    }
586→}
