588→fn format_size(bytes: u64) -> String {
589→    if bytes >= 1_000_000 {
590→        format!("{:.1}M", bytes as f64 / 1_000_000.0)
591→    } else if bytes >= 1_000 {
592→        format!("{:.1}K", bytes as f64 / 1_000.0)
593→    } else {
594→        format!("{}", bytes)
595→    }
596→}
597→
598→/// Result of a compare operation on a single package
599→pub struct CompareResult {
600→    pub op: OpResult,
601→    pub specs: Vec<SpecResult>,
602→}
603→
604→/// Compare all workspace packages that have binaries
605→///
606→/// When `tspec_patterns` is empty, each package discovers its own specs via default glob.
607→/// When non-empty, patterns are resolved per-package; packages with no matches are skipped.
608→pub fn compare_all(
609→    workspace: &WorkspaceInfo,
610→    tspec_patterns: &[String],
611→    fail_fast: bool,
612→    verbosity: Verbosity,
613→) -> Vec<CompareResult> {
614→    let normalized = match normalize_tspec_patterns(tspec_patterns) {
615→        Some(n) => n,
616→        None => {
617→            warn_shell_glob_expansion(tspec_patterns);
618→            return Vec::new();
619→        }
620→    };
621→    let mut results = Vec::new();
622→
623→    for member in workspace.buildable_members() {
624→        if !member.has_binary {
625→            continue;
626→        }
627→
628→        let spec_paths = if normalized.is_empty() {
629→            find_tspecs(&member.path, &[]).unwrap_or_default()
630→        } else {
631→            let resolved = resolve_specs_for_member(&member.path, &normalized);
632→            if resolved.is_empty() {
633→                continue;
634→            }
635→            resolved
636→        };
637→
638→        println!("=== {} ===", member.name);
639→
640→        let (op, specs) = match compare_specs(&member.name, &spec_paths, verbosity) {
641→            Ok(spec_results) => (
642→                OpResult {
643→                    name: member.name.clone(),
644→                    spec: String::new(),
645→                    success: true,
646→                    message: "ok".to_string(),
647→                    size: None,
648→                },
649→                spec_results,
650→            ),
651→            Err(e) => (
652→                OpResult {
653→                    name: member.name.clone(),
654→                    spec: String::new(),
655→                    success: false,
656→                    message: e.to_string(),
657→                    size: None,
658→                },
659→                Vec::new(),
660→            ),
661→        };
662→
663→        let failed = !op.success;
664→        results.push(CompareResult { op, specs });
665→
666→        if failed && fail_fast {
667→            break;
668→        }
669→    }
670→
671→    results
672→}
673→
674→/// Print a summary for compare operations
675→///
676→/// With a single package, just prints its comparison table.
677→/// With multiple packages, reprints all per-package tables then an overall OK/FAIL summary.
678→pub fn print_compare_summary(name: &str, results: &[CompareResult]) -> ExitCode {
679→    let has_failure = results.iter().any(|r| !r.op.success);
680→
681→    // Reprint per-package comparison tables together
682→    for result in results {
683→        if !result.specs.is_empty() {
684→            print_comparison(&result.op.name, &result.specs);
685→        }
686→    }
687→
688→    // Only show overall COMPARE SUMMARY when there are multiple packages
689→    if results.len() > 1 {
690→        let mut ok_count = 0;
691→        let mut failed_count = 0;
692→
693→        let rows: Vec<SummaryRow> = results
694→            .iter()
695→            .map(|r| {
696→                let detail = if r.op.success {
697→                    ok_count += 1;
698→                    "[ OK ]".to_string()
699→                } else {
700→                    failed_count += 1;
701→                    "[FAIL]".to_string()
702→                };
703→                SummaryRow {
704→                    name: r.op.name.clone(),
705→                    spec: r.op.spec.clone(),
706→                    detail,
707→                }
708→            })
709→            .collect();
710→
711→        print_summary_table(
712→            name,
713→            "COMPARE",
714→            "Status",
715→            &rows,
716→            &format!("Compare: {ok_count} ok, {failed_count} failed"),
717→        );
718→    }
719→
720→    if has_failure {
721→        ExitCode::from(1)
722→    } else {
723→        ExitCode::SUCCESS
724→    }
725→}
726→
727→/// Print a summary for run operations (shows exit codes, not pass/fail)
728→pub fn print_run_summary(name: &str, results: &[OpResult]) -> ExitCode {
729→    let mut error_count = 0;
730→
731→    let rows: Vec<SummaryRow> = results
732→        .iter()
733→        .map(|r| {
734→            let detail = if r.success {
735→                let code = r.message.strip_prefix("exit code: ").unwrap_or(&r.message);
736→                format!("{code:>4}")
737→            } else {
738→                error_count += 1;
739→                format!("ERROR: {}", r.message)
740→            };
741→            SummaryRow {
742→                name: r.name.clone(),
743→                spec: r.spec.clone(),
744→                detail,
745→            }
746→        })
747→        .collect();
748→
749→    let footer = if error_count > 0 {
750→        format!("Run: {error_count} error(s)")
751→    } else {
752→        String::new()
753→    };
754→
755→    print_summary_table(name, "RUN", "Exit", &rows, &footer);
756→
757→    if error_count > 0 {
758→        ExitCode::from(1)
759→    } else {
760→        ExitCode::SUCCESS
761→    }
762→}
