                from,
            } => {
                ts_cmd::new_tspec(project_root, package.as_deref(), name, from.as_deref())?;
            }
            TsCommands::Set {
                key,
                value,
                package,
                tspec,
            } => {
                ts_cmd::set_value(
                    project_root,
                    package.as_deref(),
                    key,
                    value,
                    tspec.as_deref(),
                )?;
            }
            TsCommands::Unset {
                key,
                package,
                tspec,
            } => {
                ts_cmd::unset_value(project_root, package.as_deref(), key, tspec.as_deref())?;
            }
            TsCommands::Add {
                key,
                value,
                index,
                package,
                tspec,
            } => {
                ts_cmd::add_value(
                    project_root,
                    package.as_deref(),
                    key,
                    value,
                    *index,
                    tspec.as_deref(),
                )?;
            }
            TsCommands::Remove {
