                index,
                package,
                tspec,
            } => {
                ts_cmd::add_value(
                    project_root,
                    package.as_deref(),
                    key,
                    value,
                    *index,
                    tspec.as_deref(),
                )?;
            }
            TsCommands::Remove {
                key,
                value,
                index,
                package,
                tspec,
            } => {
                ts_cmd::remove_value(
                    project_root,
                    package.as_deref(),
                    key,
                    value,
