    fn is_tspec_generated_rejects_mixed_content() {
        let content = concat!(
            "fn main() {\n",
            "    println!(\"cargo:rerun-if-changed=build.rs\");\n",
            "    println!(\"cargo:rustc-link-arg-bin=myapp=-lfoo\");\n",
