 9→fn main() -> Result<ExitCode, anyhow::Error> {
10→    let cli = Cli::parse();
11→    let flags = CargoFlags {
12→        verbosity: Verbosity::from_count(cli.verbose),
13→        jobs: cli.jobs,
14→        extra_args: Vec::new(),
15→    };
16→
17→    match cli.command {
18→        Commands::Build(cmd) => {
19→            return cmd.execute(&find_project_root()?, &flags);
20→        }
21→        Commands::Run(cmd) => {
22→            return cmd.execute(&find_project_root()?, &flags);
23→        }
24→        Commands::Test(cmd) => {
25→            return cmd.execute(&find_project_root()?, &flags);
26→        }
27→        Commands::Clean(cmd) => {
28→            cmd.execute(&find_project_root()?, &flags)?;
29→        }
30→        Commands::Clippy(cmd) => {
31→            cmd.execute(&find_project_root()?, &flags)?;
32→        }
33→        Commands::Fmt(cmd) => {
34→            cmd.execute(&find_project_root()?, &flags)?;
35→        }
36→        Commands::Compare(cmd) => {
37→            cmd.execute(&find_project_root()?, &flags)?;
38→        }
39→        Commands::Ts(cmd) => {
40→            cmd.execute(&find_project_root()?, &flags)?;
41→        }
42→        Commands::Version(cmd) => {
43→            cmd.execute(&find_project_root()?, &flags)?;
44→        }
45→        Commands::Install(cmd) => {
46→            cmd.execute(&find_project_root()?, &flags)?;
47→        }
48→    }
49→
50→    Ok(ExitCode::SUCCESS)
51→}
