31→/// Trait for command execution.
32→pub trait Execute {
33→    fn execute(&self, project_root: &Path, flags: &CargoFlags) -> Result<ExitCode>;
34→}
35→
36→/// Resolve a `-p` argument (path or name) to the actual cargo package name.
37→/// Returns Some(name) for a package, None if it resolves to a workspace root
38→/// with no `[package]` section (meaning "operate on all packages").
39→pub(crate) fn resolve_package_arg(pkg: &str) -> Result<Option<String>> {
40→    let workspace = find_project_root()?;
41→    let pkg_dir = find_package_dir(&workspace, pkg)?;
42→    match get_package_name(&pkg_dir) {
43→        Ok(name) => Ok(Some(name)),
44→        Err(_) => Ok(None),
45→    }
46→}
47→
48→/// Helper for commands that execute a cargo subcommand.
49→pub fn execute_cargo_subcommand(
50→    subcommand: &str,
51→    args: &[OsString],
52→    project_root: &Path,
53→    flags: &CargoFlags,
54→) -> Result<ExitCode> {
55→    let mut cmd = std::process::Command::new("cargo");
56→    cmd.arg(subcommand);
57→    cmd.args(args);
58→    flags.apply_to_command(&mut cmd);
59→    cmd.current_dir(project_root);
60→    let status = cmd
61→        .status()
62→        .with_context(|| format!("failed to run cargo {}", subcommand))?;
63→    if status.success() {
64→        Ok(ExitCode::SUCCESS)
65→    } else {
66→        bail!("cargo {} failed", subcommand);
67→    }
68→}
69→
70→/// Check if current directory is a package (has Cargo.toml with [package]).
71→/// Returns None at a workspace root so that commands default to all-packages mode.
72→pub(crate) fn current_package_name() -> Option<String> {
73→    let cwd = std::env::current_dir().ok()?;
74→    // At a workspace root, don't treat it as a single package
75→    if cwd.join("Cargo.toml").exists() && !is_pop(&cwd) {
76→        return None;
77→    }
78→    get_package_name(&cwd).ok()
79→}
