 1→//! Workspace discovery
 2→//!
 3→//! Uses `cargo metadata` to discover workspace members.
 4→
 5→use anyhow::{Context, Result};
 6→use cargo_metadata::MetadataCommand;
 7→use std::path::PathBuf;
 8→
 9→/// Information about a workspace package
10→#[derive(Debug, Clone)]
11→pub struct PackageMember {
12→    pub name: String,
13→    pub path: PathBuf,
14→    pub has_binary: bool,
15→}
16→
17→/// Workspace information from cargo metadata
18→pub struct WorkspaceInfo {
19→    pub root: PathBuf,
20→    pub members: Vec<PackageMember>,
21→}
22→
23→impl WorkspaceInfo {
24→    /// Workspace name derived from the root directory basename
25→    pub fn name(&self) -> &str {
26→        self.root
27→            .file_name()
28→            .and_then(|n| n.to_str())
29→            .unwrap_or("workspace")
30→    }
31→
32→    /// Discover workspace using cargo metadata
33→    pub fn discover() -> Result<Self> {
34→        let metadata = MetadataCommand::new()
35→            .no_deps()
36→            .exec()
37→            .context("failed to run cargo metadata")?;
38→
39→        let root = metadata.workspace_root.as_std_path().to_path_buf();
40→
41→        let members: Vec<PackageMember> = metadata
42→            .workspace_packages()
43→            .iter()
44→            .map(|pkg| {
45→                let path = pkg
46→                    .manifest_path
47→                    .parent()
48→                    .unwrap()
49→                    .as_std_path()
50→                    .to_path_buf();
51→                let has_binary = pkg.targets.iter().any(|t| t.is_bin());
52→
53→                PackageMember {
54→                    name: pkg.name.clone(),
55→                    path,
56→                    has_binary,
57→                }
58→            })
59→            .collect();
60→
61→        Ok(WorkspaceInfo { root, members })
62→    }
63→
64→    /// Get all workspace members
65→    pub fn buildable_members(&self) -> Vec<&PackageMember> {
66→        self.members.iter().collect()
67→    }
68→
69→    /// Get members that have binaries
70→    pub fn runnable_members(&self) -> Vec<&PackageMember> {
71→        self.members.iter().filter(|m| m.has_binary).collect()
72→    }
73→}
