78→
79→    #[test]
80→    fn discover_works() {
81→        // This test works for both workspaces and POPs
82→        let info = WorkspaceInfo::discover();
83→        if let Ok(info) = info {
84→            assert!(!info.members.is_empty());
85→
86→            // All members should be buildable
87→            assert_eq!(info.buildable_members().len(), info.members.len());
88→
89→            let _runnable = info.runnable_members();
90→            // Just verify it doesn't panic - count depends on project structure
91→        }
92→        // If cargo metadata fails (e.g., not in a Rust project), that's OK for CI
93→    }
94→}
