122→/// Check if a project root is a POP (Plain Old Package) vs a workspace
123→pub fn is_pop(project_root: &Path) -> bool {
124→    let cargo_toml = project_root.join("Cargo.toml");
125→    if let Ok(content) = std::fs::read_to_string(&cargo_toml) {
126→        // POP has [package] but no [workspace]
127→        has_toml_section_exact(&content, "package")
128→            && !has_toml_section_exact(&content, "workspace")
129→    } else {
130→        false
131→    }
132→}
