310→/// Get binary path for a build with a spec.
311→/// `cli_profile` is the CLI-specified profile (None = debug default).
312→pub fn get_binary_path(
313→    workspace: &Path,
314→    crate_name: &str,
315→    spec: &Spec,
316→    cli_profile: Option<&str>,
317→    expanded_target_dir: Option<&str>,
318→) -> PathBuf {
319→    let target = spec.cargo.target_triple.clone().or_else(|| {
320→        spec.cargo
321→            .target_json
322→            .as_ref()
323→            .and_then(|p| p.file_stem().map(|s| s.to_string_lossy().to_string()))
324→    });
325→
326→    // Determine profile directory: spec profile takes precedence, then CLI profile
327→    let dir = match spec.cargo.profile.as_deref() {
328→        Some(p) => profile_dir_name(p),
329→        None => match cli_profile {
330→            Some(p) => profile_dir_name(p),
331→            None => "debug",
332→        },
333→    };
334→
335→    let base = match expanded_target_dir {
336→        Some(td) => workspace.join("target").join(td),
337→        None => workspace.join("target"),
338→    };
339→
340→    match target {
341→        Some(t) => base.join(t).join(dir).join(crate_name),
342→        None => base.join(dir).join(crate_name),
343→    }
344→}
345→
346→/// Get binary path for a simple build (no spec).
347→/// `cli_profile` is the CLI-specified profile (None = debug default).
348→pub fn get_binary_path_simple(
349→    workspace: &Path,
350→    crate_name: &str,
351→    cli_profile: Option<&str>,
352→) -> PathBuf {
353→    let dir = match cli_profile {
354→        Some(p) => profile_dir_name(p),
355→        None => "debug",
356→    };
357→    workspace.join("target").join(dir).join(crate_name)
358→}
