368→    // ==================== get_package_name tests ====================
369→
370→    #[test]
371→    fn get_package_name_valid() {
372→        let tmp = TempDir::new().unwrap();
373→        let cargo_toml = tmp.path().join("Cargo.toml");
374→        fs::write(
375→            &cargo_toml,
376→            r#"[package]
377→name = "my-test-crate"
378→version = "0.1.0"
379→"#,
380→        )
381→        .unwrap();
382→
383→        let name = get_package_name(tmp.path()).unwrap();
384→        assert_eq!(name, "my-test-crate");
385→    }
386→
387→    #[test]
388→    fn get_package_name_with_single_quotes() {
389→        let tmp = TempDir::new().unwrap();
390→        let cargo_toml = tmp.path().join("Cargo.toml");
391→        fs::write(
392→            &cargo_toml,
393→            "[package]\nname = 'single-quoted'\nversion = '0.1.0'\n",
394→        )
395→        .unwrap();
396→
397→        let name = get_package_name(tmp.path()).unwrap();
398→        assert_eq!(name, "single-quoted");
