401→    #[test]
402→    fn get_package_name_missing_name() {
403→        let tmp = TempDir::new().unwrap();
404→        let cargo_toml = tmp.path().join("Cargo.toml");
405→        fs::write(&cargo_toml, "[package]\nversion = \"0.1.0\"\n").unwrap();
406→
407→        let result = get_package_name(tmp.path());
408→        assert!(result.is_err());
409→        assert!(
410→            result
411→                .unwrap_err()
412→                .to_string()
413→                .contains("could not find package name")
414→        );
415→    }
