 14→/// Build package(s) with a translation spec
 15→#[derive(Args)]
 16→pub struct BuildCmd {
 17→    /// Package to build (name or path, e.g. "." for current dir)
 18→    #[arg(value_name = "PACKAGE")]
 19→    pub positional: Option<String>,
 20→    /// Package to build (defaults to current directory or all packages)
 21→    #[arg(short = 'p', long = "package")]
 22→    pub package: Option<String>,
 23→    /// Build all workspace packages (even when in a package directory)
 24→    #[arg(short = 'w', long = "workspace")]
 25→    pub workspace: bool,
 26→    /// Spec file(s) or glob pattern(s) to build with (defaults to package's tspec file)
 27→    #[arg(short = 't', long = "tspec", num_args = 1..)]
 28→    pub tspec: Vec<String>,
 29→    /// Release build
 30→    #[arg(short, long, conflicts_with = "profile")]
 31→    pub release: bool,
 32→    /// Build profile (e.g., release, release-small, or any custom profile)
 33→    #[arg(long)]
 34→    pub profile: Option<String>,
 35→    /// Strip symbols from binary after build
 36→    #[arg(short, long)]
 37→    pub strip: bool,
 38→    /// Stop on first failure (for all-packages mode)
 39→    #[arg(short, long)]
 40→    pub fail_fast: bool,
 41→}
 42→
 43→impl BuildCmd {
 44→    /// Resolve the effective profile from --release / --profile flags.
 45→    fn effective_profile(&self) -> Option<&str> {
 46→        if let Some(ref p) = self.profile {
 47→            Some(p)
 48→        } else if self.release {
 49→            Some("release")
 50→        } else {
 51→            None
 52→        }
 53→    }
 54→}
 55→
 56→impl Execute for BuildCmd {
 57→    fn execute(&self, project_root: &Path, flags: &CargoFlags) -> Result<ExitCode> {
 58→        let cli_profile = self.effective_profile();
 59→
 60→        // Resolve package: --workspace > -p/positional PKG > cwd > all
 61→        let resolved = if self.workspace {
 62→            None
 63→        } else {
 64→            match self.positional.as_deref().or(self.package.as_deref()) {
 65→                Some(pkg) => resolve_package_arg(pkg)?,
 66→                None => current_package_name(),
 67→            }
 68→        };
 69→
 70→        match resolved {
 71→            None => {
 72→                let workspace = WorkspaceInfo::discover()?;
 73→                let results = build_all(
 74→                    &workspace,
 75→                    &self.tspec,
 76→                    cli_profile,
 77→                    self.strip,
 78→                    self.fail_fast,
 79→                    flags,
 80→                );
 81→                Ok(print_summary(workspace.name(), &results))
 82→            }
 83→            Some(name) => {
 84→                if self.tspec.is_empty() {
 85→                    let result = build_package(&name, None, cli_profile, flags)?;
 86→                    if self.strip {
 87→                        strip_binary(&result.binary_path)?;
 88→                    }
 89→                } else {
 90→                    let package_dir = resolve_package_dir(project_root, Some(&name))?;
 91→                    let pkg_name = get_package_name(&package_dir)?;
 92→                    let spec_paths = find_tspecs(&package_dir, &self.tspec)?;
 93→                    for spec_path in &spec_paths {
 94→                        let spec_str = spec_path.to_string_lossy();
 95→                        let result = build_package(&pkg_name, Some(&spec_str), cli_profile, flags)?;
 96→                        if self.strip {
 97→                            strip_binary(&result.binary_path)?;
 98→                        }
 99→                    }
100→                }
101→                Ok(ExitCode::SUCCESS)
102→            }
103→        }
104→    }
105→}
