 13→/// Parsed test statistics from cargo test output.
 14→#[derive(Debug, Default, Clone)]
 15→pub struct TestResult {
 16→    pub passed: u32,
 17→    pub failed: u32,
 18→    pub ignored: u32,
 19→    pub filtered: u32,
 20→}
 21→
 22→impl TestResult {
 23→    /// Aggregate another TestResult into this one.
 24→    pub fn merge(&mut self, other: &TestResult) {
 25→        self.passed += other.passed;
 26→        self.failed += other.failed;
 27→        self.ignored += other.ignored;
 28→        self.filtered += other.filtered;
 29→    }
 30→
 31→    /// Total tests that actually ran (passed + failed).
 32→    pub fn total_ran(&self) -> u32 {
 33→        self.passed + self.failed
 34→    }
 35→}
 36→
 37→/// Parse a `test result:` line from cargo test output.
 38→///
 39→/// Example: `test result: ok. 263 passed; 0 failed; 0 ignored; 0 measured; 0 filtered out; finished in 0.42s`
 40→pub fn parse_test_result_line(line: &str) -> Option<TestResult> {
 41→    let rest = line.strip_prefix("test result:")?;
 42→    let rest = rest.trim();
 43→    // Skip the "ok." or "FAILED." prefix
 44→    let rest = rest
 45→        .strip_prefix("ok.")
 46→        .or_else(|| rest.strip_prefix("FAILED."))?;
 47→    let rest = rest.trim();
 48→
 49→    let mut result = TestResult::default();
 50→    for part in rest.split(';') {
 51→        let part = part.trim();
 52→        if let Some((num, label)) = part.split_once(' ') {
 53→            let Ok(n) = num.parse::<u32>() else {
 54→                continue;
 55→            };
 56→            match label.trim() {
 57→                "passed" => result.passed = n,
 58→                "failed" => result.failed = n,
 59→                "ignored" => result.ignored = n,
 60→                "filtered" | "filtered out" => result.filtered = n,
 61→                _ => {} // "measured"
 62→            }
 63→        }
 64→    }
 65→    Some(result)
 66→}
 67→
 68→/// Parse and aggregate raw `test result:` lines into a single TestResult.
 69→pub fn parse_test_results(lines: &[String]) -> TestResult {
 70→    let mut aggregated = TestResult::default();
 71→    for line in lines {
 72→        if let Some(parsed) = parse_test_result_line(line) {
 73→            aggregated.merge(&parsed);
 74→        }
 75→    }
 76→    aggregated
 77→}
